import React, { useEffect } from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";

import { flexbox } from "../../../style/mixins";

import Task from "../Task/Task.jsx";

const Todo = () => {
  const loggedUser = useSelector(state => state.userReducer[0]);

  useEffect(() => {});

  const todoTasks = loggedUser.todoArray.map((task, index) => {
    return (
      <Task
        key={index}
        title={task.title}
        description={task.description}
        deadline={task.deadline}
        colorWay={task.colorWay}
      />
    );
  });

  return <Wrapper id="todo">{<TasksList>{todoTasks}</TasksList>}</Wrapper>;
};

export default Todo;

const Wrapper = styled.div`
  vertical-align: top;
  ${flexbox()};
  scroll-snap-align: center;
  scroll-snap-stop: always;
  background: lightblue;
  display: inline-block;
  width: 100%;
  height: 100%;
`;

const TasksList = styled.div`
  /* ${flexbox({ dir: "column", jc: "flex-start" })}; */
  height: 100%;
  width: 100%;
  overflow-y: auto;
`;
