import React, { useState } from "react";
import styled from "styled-components";
import { flexbox } from "../../style/mixins";
import { useSelector, useDispatch } from "react-redux";
import _ from "lodash";

import { getUserAction } from "../../state/actions/getUserAction";
import { updateArraysAction } from "../../state/actions/tasksArraysAction";

const Task = ({ title, description, deadline, colorway, status, id }) => {
  const [currentStatus, setCurrentStatus] = useState(status);
  const [cardIsFlipped, setCardIsFlipped] = useState(false);

  const dispatch = useDispatch();
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArrays = useSelector(state => state.updateTasksArraysReducer);

  const firstMoveOption = () => {
    switch (currentStatus) {
      case "todo":
        return "Forward to In Progress";
        break;
      case "inProgress":
        return "Forward to Done";
        break;
      case "done":
        return "Return to In Progress";
        break;

      default:
        break;
    }
  };

  const secondMoveOption = () => {
    switch (currentStatus) {
      case "todo":
        return "Forward to Done";
        break;
      case "inProgress":
        return "Return to Todo";
        break;
      case "done":
        return "Return to Todo";
        break;

      default:
        break;
    }
  };

  const selectChange = (arrayToRemoveFrom, arrayToAddTo, newStatus) => {
    const selectedTask = _.remove(arrayToRemoveFrom, n => {
      return n.id === id;
    });
    arrayToAddTo.push(...selectedTask);

    const updatedArrays = {
      todoArray: arrayToRemoveFrom,
      inProgressArray: arrayToAddTo
    };

    dispatch(updateArraysAction(updatedArrays));
  };

  const handleFirstMove = () => {
    switch (currentStatus) {
      case "todo":
        setCurrentStatus("inProgress");
        return selectChange(tasksArrays.todoArray, tasksArrays.inProgressArray);

        break;
      case "inprogress":
        setCurrentStatus("done");
        return selectChange(
          tasksArrays.inProgressArray,
          tasksArrays.completedTasksArray
        );

        // push > done
        break;
      case "done":
        setCurrentStatus("inProgress");
        return selectChange(
          tasksArrays.completedTasksArray,
          tasksArrays.inProgressArray
        );
        // push back > in progress
        break;
      default:
        break;
    }
  };

  const handleSecondMove = () => {
    switch (status) {
      case "todo":
        setCurrentStatus("done");
        return selectChange(
          tasksArrays.todoArray,
          tasksArrays.completedTasksArray
        );
        // push > in Done
        break;
      case "inprogress":
        setCurrentStatus("todo");
        return selectChange(tasksArrays.inProgressArray, tasksArrays.todoArray);
        // push back > todo
        break;
      case "done":
        setCurrentStatus("todo");
        return selectChange(
          tasksArrays.completedTasksArray,
          tasksArrays.todoArray
        );
        // push back > todo
        break;

      default:
        break;
    }
  };

  const handleDelete = () => {};

  return (
    <TaskContainer cardIsFlipped={cardIsFlipped}>
      <FaceSide>
        <OptionsButton onClick={() => setCardIsFlipped(!cardIsFlipped)}>
          <div>flip</div>
        </OptionsButton>
        <DetailsSection>
          <Title>{title}</Title>
          <Description>{description}</Description>
          <Deadline>{deadline}</Deadline>
        </DetailsSection>
      </FaceSide>
      <BackSide>
        <OptionsButton onClick={() => setCardIsFlipped(!cardIsFlipped)}>
          <div>flip</div>
        </OptionsButton>

        <MoveToButton onClick={handleFirstMove}>{firstMoveOption}</MoveToButton>
        <MoveToButton onClick={handleSecondMove}>
          {secondMoveOption}
        </MoveToButton>
        <DeleteTaskButton onClick={handleDelete}>
          Delete Tracker
        </DeleteTaskButton>
      </BackSide>
    </TaskContainer>
  );
};

export default Task;

const TaskContainer = styled.div`
  ${flexbox()}
  border-radius: 5px;
  height: 30%;
  width: 90%;
  margin: 20px auto;
  position: relative;
  background: white;
  border-radius: 10px;

  transition: transform 0.8s;
  transform-style: preserve-3d;

  transform: ${({ cardIsFlipped }) =>
    cardIsFlipped
      ? "perspective(2000) rotateY(180deg)"
      : "perspective(2000) rotateY(0deg)"};
`;

const FaceSide = styled.div`
  position: absolute;
  backface-visibility: hidden;
  height: 100%;
  width: 100%;
  perspective: 900px;
`;

const OptionsButton = styled.div`
  position: absolute;
  right: 0;
  top: 0;
  height: 20%;
  width: 10%;
  background: lightsteelblue;
  border-radius: 10px 10px 0 10px;

  div {
    ${flexbox()};

    height: 100%;
    width: 100%;
    border-radius: 10px;
    background: lightcoral;
  }
`;

const DetailsSection = styled.div`
  ${flexbox({ dir: "column", jc: "space-around" })}
  height: 100%;
  width: 100%;
`;

const Title = styled.div`
  flex-basis: 20%;
  height: 20%;
  width: 100%;
  font-size: 1.3rem;
  font-weight: bold;
  padding: 5px;
`;

const Description = styled.div`
  flex-basis: 60%;
  height: 70%;
  width: 100%;
  border-radius: 10px 0 0 0;
  background: lightsteelblue;
  padding: 5px;
`;

const Deadline = styled.div`
  flex-basis: 20%;
  height: 10%;

  width: 100%;
  background: lightsteelblue;
  border-radius: 0 0 10px 10px;
  padding: 5px;
`;

const BackSide = styled.div`
  position: absolute;
  backface-visibility: hidden;

  transform: rotateY(180deg);
  perspective: 900px;

  height: 100%;
  width: 100%;
`;

const MoveToButton = styled.div`
  width: 100%;
  border: 1px solid black;
  height: 33%;
`;

const DeleteTaskButton = styled.div`
  height: 33%;
`;
