import { filteringArraysAction } from "../actions/filteredTasksAction";

const filteringArraysReducer = (state = [], action) => {
  switch (action.type) {
    case filteringArraysAction:
      return [...state, ...action.payload];
      break;
    default:
      return state;
      break;
  }
};

export default filteringArraysReducer;
