import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import _ from "lodash";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import { getUserAction } from "../../../state/actions/getUserAction";
import { updateArraysAction } from "../../../state/actions/updateArraysAction";

const Task = ({
  title,
  description,
  deadline,
  colorway,
  status,
  id,
  firstButtonText,
  secondButtonText,
  setArrayLengthChekcer
}) => {
  const [cardIsFlipped, setCardIsFlipped] = useState(false);

  const dispatch = useDispatch();
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArrays = useSelector(state => state.updateArraysReducer);

  const todoArray = tasksArrays.todoArray;
  const inProgressArray = tasksArrays.inProgressArray;
  const completedTasksArray = tasksArrays.completedTasksArray;

  //--SERVER FUNCTIONS--//

  const updatingArraysInDataBase = async () => {
    const updatedArrays = {
      todoArray,
      inProgressArray,
      completedTasksArray
    };

    const putMethod = {
      method: "PUT",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
        "auth-token": localStorage.getItem("token")
      },
      body: JSON.stringify(updatedArrays) // We send data in JSON format
    };

    await fetch(
      `https://tracker-database.youngwebdevs.now.sh/dashboard/updatingarrays`,
      putMethod
    );

    console.log("PUT ENDED");
  };

  const deletingFromArrayInDataBase = async () => {
    const updatedArray = {
      todoArray,
      inProgressArray,
      completedTasksArray
    };

    const putMethod = {
      method: "PUT",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
        "auth-token": localStorage.getItem("token")
      },
      body: JSON.stringify(updatedArray) // We send data in JSON format
    };

    await fetch(
      `https://tracker-database.youngwebdevs.now.sh/dashboard/removetask`,
      putMethod
    );
  };

  //--------------//

  const selectChange = (
    arrayToRemoveFrom,
    arrayToAddInto,
    destination,
    deleteTask
  ) => {
    const selectedTask = _.remove(arrayToRemoveFrom, n => {
      return n.id === id;
    });
    console.log("selectedtask: ", selectedTask[0]);

    if (deleteTask === true) {
      setCardIsFlipped(false);
      // setRefreshCounter(refreshCounter + 1);
      dispatch(updateArraysAction(arrayToRemoveFrom));
      deletingFromArrayInDataBase();
      return;
    }

    const newStatusSelectedTask = {
      ...selectedTask[0],
      status: destination
    };
    arrayToAddInto.push(newStatusSelectedTask);

    const updatedArrays = {
      arrayToRemoveFrom,
      arrayToAddInto
    };

    setCardIsFlipped(false);
    // setRefreshCounter(refreshCounter + 1);
    dispatch(updateArraysAction(updatedArrays));
  };

  const handleFirstMove = () => {
    switch (status) {
      case "todo":
        selectChange(todoArray, inProgressArray, "inProgress");
        updatingArraysInDataBase(todoArray, inProgressArray);
        break;
      case "inProgress":
        selectChange(inProgressArray, completedTasksArray, "done");
        updatingArraysInDataBase(inProgressArray, completedTasksArray);
        break;
      case "done":
        selectChange(completedTasksArray, inProgressArray, "inProgress");
        updatingArraysInDataBase(completedTasksArray, inProgressArray);
        break;
      default:
        break;
    }
  };

  const handleSecondMove = () => {
    switch (status) {
      case "todo":
        selectChange(todoArray, completedTasksArray, "done");
        updatingArraysInDataBase(todoArray, completedTasksArray);
        break;
      case "inProgress":
        return selectChange(inProgressArray, todoArray, "todo");
        updatingArraysInDataBase(inProgressArray, todoArray);
        break;
      case "done":
        return selectChange(completedTasksArray, todoArray, "todo");
        updatingArraysInDataBase(completedTasksArray, todoArray);
        break;
      default:
        break;
    }
  };

  const handleDelete = () => {
    switch (status) {
      case "todo":
        selectChange(todoArray, null, null, true);
        break;
      case "inProgress":
        return selectChange(inProgressArray, null, null, true);
        break;
      case "done":
        return selectChange(completedTasksArray, null, null, true);
        break;
      default:
        break;
    }
  };

  return (
    <TaskContainer cardIsFlipped={cardIsFlipped}>
      <FaceSide>
        <OptionsButton onClick={() => setCardIsFlipped(!cardIsFlipped)}>
          <div>flip</div>
        </OptionsButton>
        <DetailsSection>
          <Title>{title}</Title>
          <Description>{description}</Description>
          <Deadline>{deadline}</Deadline>
        </DetailsSection>
      </FaceSide>
      <BackSide>
        <OptionsButtonBack onClick={() => setCardIsFlipped(!cardIsFlipped)}>
          <div>flip</div>
        </OptionsButtonBack>

        <FirstMoveButton
          onClick={() => {
            handleFirstMove(status);
          }}
        >
          {firstButtonText}
          <HrMarker />
        </FirstMoveButton>
        <SecondMoveButton
          onClick={() => {
            handleSecondMove(status);
          }}
        >
          {secondButtonText}
          <HrMarker />
        </SecondMoveButton>
        <DeleteTaskButton
          onClick={() => {
            handleDelete(status);
          }}
        >
          Delete Tracker
          <HrMarker />
        </DeleteTaskButton>
      </BackSide>
    </TaskContainer>
  );
};

export default Task;

const TaskContainer = styled.div`
  ${flexbox()}
  border-radius: 5px;
  height: 30%;
  max-height: 200px;
  width: 90%;
  max-width: 500px;
  margin: 20px auto;
  position: relative;
  background: white;
  border-radius: 10px;

  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);

  transition: transform 0.8s;
  transform-style: preserve-3d;

  transform: ${({ cardIsFlipped }) =>
    cardIsFlipped
      ? "perspective(2000) rotateY(180deg)"
      : "perspective(2000) rotateY(0deg)"};
`;

const FaceSide = styled.div`
  position: absolute;
  backface-visibility: hidden;
  height: 100%;
  width: 100%;
  perspective: 900px;
`;

const OptionsButton = styled.div`
  position: absolute;
  z-index: 2;
  right: 0;
  top: 0;
  height: 30%;
  width: 50px;
  background: ${c.bgSlateBlue};
  border-radius: 10px 10px 0 10px;

  :hover {
    cursor: pointer;
  }

  div {
    ${flexbox()};
    height: 100%;
    width: 100%;
    border-radius: 10px;
    background: lightcoral;
    transition: 0.3s ease-in-out;
    :hover {
      transform: scale(1.02) translateX(2px) translateY(-4px);
      box-shadow: -2px 2px 6px ${c.darkText};
    }
  }
`;

const DetailsSection = styled.div`
  ${flexbox({ dir: "column", jc: "space-around" })}
  height: 100%;
  width: 100%;
`;

const Title = styled.div`
  ${flexbox({ jc: "flex-start" })}
  flex-basis: 30%;
  height: 30%;
  width: 100%;
  font-size: 1.5rem;
  font-weight: bold;
  padding-left: 10px;

  color: ${c.darkText};
`;

const Description = styled.div`
  ${flexbox({ jc: "flex-start", ai: "flex-start" })}
  flex-basis: 60%;
  height: 60%;
  width: 100%;
  border-radius: 10px 0 0 0;
  background: ${c.bgSlateBlue};
  padding: 10px;
  color: white;
  font-size: 0.9rem;
`;

const Deadline = styled.div`
  ${flexbox({ jc: "flex-start", ai: "flex-end" })}
  flex-basis: 10%;
  height: 10%;

  width: 100%;
  background: ${c.bgSlateBlue};
  border-radius: 0 0 10px 10px;
  font-weight: bold;
  padding: 0 0 8px 10px;
  color: ${c.shyRed};
  font-size: 1rem;
`;

const BackSide = styled.div`
  position: absolute;
  backface-visibility: hidden;

  transform: rotateY(180deg);
  perspective: 900px;

  height: 100%;
  width: 100%;
`;

const OptionsButtonBack = styled(OptionsButton)`
  height: 33.333%;
`;

const HrMarker = styled.hr`
  width: 70%;
  height: 5px;
  border-radius: 20px;
  border: none;
  transition: 0.3s ease-out;
  position: absolute;
  bottom: 0;
`;

const FirstMoveButton = styled.div`
  position: relative;
  ${flexbox({ dir: "column" })};
  width: 100%;
  height: 33.333%;
  border-radius: 10px 10px 0 0;
  font-weight: bold;
  transition: 0.2s ease-out;
  :hover {
    cursor: pointer;
  }

  :hover ${HrMarker} {
    background: #4da6ff;
    box-shadow: inset 2px 2px 4px #9bcdff;
  }
`;

const SecondMoveButton = styled(FirstMoveButton)`
  border-radius: 10px 0 0 0;
  background: ${c.bgSlateBlue};
  color: white;
`;

const DeleteTaskButton = styled(SecondMoveButton)`
  border-radius: 0 0 10px 10px;
  color: ${c.shyRed};
`;
