import { UPDATEARRAYS } from "../actions/updateTodoArrayAction";

const updateTaksArraysReducer = (state = {}, action) => {
  switch (action.type) {
    case UPDATEARRAYS:
      return action.payload;
      break;

    default:
      break;
  }
};

export default updateTaksArraysReducer;
