import React from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import { Redirect, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { flexbox } from "../../style/mixins";

//-------Components-------//
import Todo from "./Todo/Todo";
import InProgress from "./InProgress/InProgress";
import Done from "./Done/Done";

//-------Actions--------//
import { importUserAction } from "../../state/actions/importUserAction";
import { isLoggedAction } from "../../state/actions/isLoggedAction";

const Dashbord = () => {
  const dispatch = useDispatch();
  const isLogged = useSelector(state => state.isLoggedReducer);
  const signOut = () => {
    dispatch(importUserAction({}));
    dispatch(isLoggedAction({}));
  };
  return (
    <Wrapper>
      {/* {isLogged === false && <Redirect push to="/" />} */}
      <AppContainer>
        <HeaderContainer>
          <Header>Tracker</Header>
          <LogOutButton onClick={signOut}>Log Out</LogOutButton>
        </HeaderContainer>

        <TabsContainer>
          <TodoTab />
          <InProgressTab />
          <DoneTab />
        </TabsContainer>
        <TrackersContainer>
          <Todo />
          <InProgress />
          <Done />
        </TrackersContainer>
      </AppContainer>
    </Wrapper>
  );
};

export default Dashbord;

const Wrapper = styled.div`
  ${flexbox()};
  height: 100%;
  width: 100%;
  box-sizing: border-box;
`;

const AppContainer = styled.button`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 100%;
  width: 100%;
  height: 100vh;
  max-width: 500px;
  border: 1px solid red;
  box-sizing: border-box;
`;

const HeaderContainer = styled.div`
  ${flexbox()};
  width: 100%;
  border: 1px solid red;
  box-sizing: border-box;
`;

const Header = styled.h1`
  font-size: 2rem;
  box-sizing: border-box;
`;

const LogOutButton = styled.button``;

const TabsContainer = styled.div`
  width: 100%;
  flex-basis: 10%;
  border: 1px solid blue;
`;

const TodoTab = styled.div``;

const InProgressTab = styled.div``;

const DoneTab = styled.div``;

const TrackersContainer = styled.div``;
