import React, { useState } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";

import { flexbox } from "../../../style/mixins";

import Task from "../Task";

const Todo = () => {
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArrays = useSelector(state => state.updateTasksArraysReducer);
  const [arrayLengthChecker, setArrayLengthChekcer] = useState(
    tasksArrays.todoArray.length
  );

  useEffect(() => {
    todoTasks();
  }, [arrayLengthChecker]);
  // const [todoArrayTest, setTodoArray] = useState([
  //   {
  //     title: "Go to the bank",
  //     description: "go and get money",
  //     deadline: "today",
  //     colorway: "blue",
  //     _id: 1
  //   },
  //   {
  //     title: "Go to the mall",
  //     description: "go and get money",
  //     deadline: "today",
  //     colorway: "red",
  //     _id: 2
  //   }
  // ]);

  const todoTasks = () => {
    return loggedUser.todoArray.map((task, index) => {
      return (
        <Task
          id={task.id}
          key={index}
          title={task.title}
          description={task.description}
          deadline={task.deadline}
          colorway={task.colorway}
          status={task.status}
          setArrayLengthChekcer={setArrayLengthChekcer}
        />
      );
    });
  };

  // const todoTasks = todoArrayTest.map((task, index) => {
  //   return (
  //     <Task
  //       _id={task._id}
  //       title={task.title}
  //       description={task.description}
  //       deadline={task.deadline}
  //       colorway={task.colorway}
  //     />
  //   );
  // });

  return <Wrapper id="todo">{<TasksList>{todoTasks}</TasksList>}</Wrapper>;
};

export default Todo;

const Wrapper = styled.div`
  vertical-align: top;
  ${flexbox()};
  scroll-snap-align: center;
  scroll-snap-stop: always;
  background: lightblue;
  display: inline-block;
  width: 100%;
  height: 100%;
`;

const TasksList = styled.div`
  /* ${flexbox({ dir: "column", jc: "flex-start" })}; */
  height: 100%;
  width: 100%;
  overflow-y: auto;
`;
