import React, { useState } from "react";
import styled from "styled-components";
import Task from "./Task/Task";
import { flexbox } from "../../style/mixins";

const TasksLister = ({ arrayToMap, firstButtonText, secondButtonText }) => {
  const [refreshCounter, setRefreshCounter] = useState(0);

  const todoTasks = () => {
    return arrayToMap.map((task, index) => {
      return (
        <Task
          id={task.id}
          key={index}
          title={task.title}
          description={task.description}
          deadline={task.deadline}
          colorway={task.colorway}
          status={task.status}
          refreshCounter={refreshCounter}
          setRefreshCounter={setRefreshCounter}
          firstButtonText={firstButtonText}
          secondButtonText={secondButtonText}
        />
      );
    });
  };

  return <TasksList>{todoTasks()}</TasksList>;
};

export default TasksLister;

const TasksList = styled.div`
  /* ${flexbox({ dir: "column", jc: "flex-start" })}; */
  height: 100%;
  width: 100%;
  overflow-y: auto;
`;
