import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Task from "./Task/Task";
import { flexbox } from "../../style/mixins";
import { useSelector } from "react-redux";

const TasksLister = ({ arrayToMap }) => {
  const toggleSort = useSelector(state => state.toggleSortReducer);
  useEffect(() => {
    sortArray();
    console.log("toggleSort", toggleSort);
  }, [toggleSort]);

  const sortArray = () => {
    //<--IF (toggleSort === true) THE TASKS SORTED BY DEADLINE-->//
    const compare = (a, b) => {
      const timeA = a.dateAndTimeSum;
      const timeB = b.dateAndTimeSum;

      let comparison = 0;
      if (timeA > timeB) {
        comparison = 1;
      } else if (timeA < timeB) {
        comparison = -1;
      }
      return comparison;
    };
    arrayToMap.sort(compare);
    //<--IF (toggleSort === false) THE TASKS SORTED BY URGENCY-->//
    if (toggleSort === false) {
      const compare = (a, b) => {
        let urgencyA;
        let urgencyB;
        // const urgencyNumberA = () => {
        switch (a.urgency) {
          case "low":
            return (urgencyA = 0);
            break;
          case "regular":
            return (urgencyA = 1);
            break;
          case "high":
            return (urgencyA = 2);
            break;
        }
        // };

        // const urgencyNumberB = () => {
        switch (b.urgency) {
          case "low":
            return (urgencyB = 0);
            break;
          case "regular":
            return (urgencyB = 1);
            break;
          case "high":
            return (urgencyB = 2);
            break;
        }
        // };

        // const urgencyA = urgencyNumberA();
        // const urgencyB = urgencyNumberB();

        let comparison = 0;
        if (urgencyA < urgencyB) {
          comparison = 1;
        } else if (urgencyA > urgencyB) {
          comparison = -1;
        }
        return comparison;
      };

      arrayToMap.sort(compare);
    }

    // <----------------------------->
  };

  const tasksMap = () => {
    return arrayToMap.map((task, index) => {
      return (
        <Task
          id={task.id}
          key={index}
          dayText={task.dayText}
          monthText={task.monthText}
          dayNumber={task.dayNumber}
          dateAndTimeSum={task.dateAndTimeSum}
          time={task.time}
          year={task.year}
          title={task.title}
          description={task.description}
          deadline={task.deadline}
          colorway={task.colorway}
          urgency={task.urgency}
          status={task.status}
        />
      );
    });
  };

  return <TasksList>{tasksMap()}</TasksList>;
};

export default TasksLister;

const TasksList = styled.div`
  ${flexbox({ jc: "flex-start", ai: "flex-start" })}
  align-content: flex-start;
  padding: 20px;
  flex-wrap: wrap;
  height: 100%;
  width: 100%;
  overflow-y: auto;
`;
