import React from "react";
import styled from "styled-components";
import { flexbox } from "../../style/mixins";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, Link } from "react-router-dom";
import * as c from "../../style/colors";

import { getUserAction } from "../../state/actions/getUserAction";
import { isLoggedAction } from "../../state/actions/isLoggedAction";

const Login = () => {
  const isLogged = useSelector(state => state.isLoggedReducer);
  const dispatch = useDispatch();

  const sendForm = async e => {
    e.preventDefault();
    const userName = e.target[0].value;
    const userPassword = e.target[1].value;

    try {
      const userData = await fetch(
        `https://tracker-database.youngwebdevs.now.sh/login/?userName=${userName}&password=${userPassword}`
      );
      console.log(userData.headers.get("auth-token"));
      const jsonUserData = await userData.json();
      // dispatch({ type: "IMPORTUSER", payload: jsonUserData });
      dispatch(getUserAction(jsonUserData));
      dispatch(isLoggedAction(jsonUserData));
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Wrapper>
      {isLogged === true && <Redirect push to="/dashboard" />}
      <Header>
        Dead<span>l</span>ine
      </Header>
      <Container>
        <FormBox onSubmit={sendForm}>
          <EmailInput type="text" placeholder="User Name..." />
          <PasswordInput type="password" placeholder="Password..." />
          <LoginButton type="submit">Log In</LoginButton>
          <SignUpButton>
            <StyledLink to="/signup">Sign Up</StyledLink>
          </SignUpButton>
          <GuestButton>Log In as Guest</GuestButton>
        </FormBox>
      </Container>
    </Wrapper>
  );
};

export default Login;

const Wrapper = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  flex-direction: column;

  color: ${c.darkText};
`;

const Header = styled.div`
  flex-basis: 10%;
  font-size: 4rem;
  font-family: "Titillium Web", sans-serif;
  color: ${c.darkBlueText};
  span {
    color: red;
  }
`;

const Container = styled.div`
  flex-basis: 65%;
  width: 80%;
  border-radius: 15px;
  box-shadow: -6px -6px 20px 0 rgba(242, 248, 250, 0.9),
    6px 6px 16px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
  padding: 20px;

  max-height: 375px;
  max-width: 400px;

  font-family: "Montserrat", sans-serif;

  display: flex;
  justify-content: center;
  align-items: center;
`;

const FormBox = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;

  width: 90%;
  height: 100%;
`;

const EmailInput = styled.input`
  width: 100%;
  height: 15%;
  border-radius: 5px;
  border: none;
  background: rgba(242, 248, 250, 0.55);
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.25),
    inset -2px -2px 8px rgba(242, 248, 250, 0.9);
  padding-left: 10px;
  font-size: 0.8rem;
  color: ${c.darkBlueText};

  opacity: 0.7;
  transition: 0.3s ease-out;

  outline: none;

  &:hover,
  &:focus {
    opacity: 1;
  }
`;
const PasswordInput = styled(EmailInput)`
  margin-bottom: 10px;
`;

const LoginButton = styled.button`
  width: 65%;
  height: 11%;
  border-radius: 5px;
  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);
  border: none;
  background: ${c.shyBlue};
  font-size: 0.9rem;
  margin-bottom: 5px;
  color: ${c.darkBlueText};
  text-shadow: 0 0 1px white;
  opacity: 0.5;
  transition: 0.3s ease-out;

  outline: none;

  &:hover,
  &:focus {
    opacity: 0.9;
    cursor: pointer;
    background: ${c.lightBlue};
  }
`;

const SignUpButton = styled.div`
  ${flexbox()};
  width: 65%;
  height: 11%;
  border-radius: 5px;
  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);
  border: none;
  background: ${c.shyBlue};
  font-size: 0.9rem;
  margin-bottom: 5px;
  color: ${c.darkBlueText};
  text-shadow: 0 0 1px white;
  opacity: 0.5;
  transition: 0.3s ease-out;

  outline: none;

  &:hover,
  &:focus {
    opacity: 0.9;
    cursor: pointer;
    background: ${c.lightBlue};
  }
`;
const GuestButton = styled(SignUpButton)``;

const StyledLink = styled(Link)`
  ${flexbox()};
  height: 100%;
  width: 100%;
  text-decoration: none;
  color: inherit;
`;
