import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import { Redirect, Link, useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";
import { GiClick } from "react-icons/gi";
import { FaHamburger } from "react-icons/fa";
import { FiSearch } from "react-icons/fi";
import { MdKeyboardArrowRight } from "react-icons/md";

//-------Components-------//
import Todo from "./Todo/Todo";
import InProgress from "./InProgress/InProgress";
import Done from "./Done/Done";
import TaskCreation from "./TaskCreation/TaskCreation";
import Search from "./Search";

//-------Actions--------//
import { getUserAction } from "../../state/actions/getUserAction";
import { isLoggedAction } from "../../state/actions/isLoggedAction";
import { updateArraysAction } from "../../state/actions/updateArraysAction";
import { filteringArraysAction } from "../../state/actions/filteredTasksAction";

const Dashbord = () => {
  const [popupIsOpen, setPopupIsOpen] = useState(false);
  const [flag, setFlag] = useState(true);
  const [dropDownIsOpen, setDropDownIsOpen] = useState(false);
  const [searchIsOpen, setSearchIsOpen] = useState(false);
  const [searchInput, setSearchInput] = useState("");

  const currentTab = useSelector(state => state.markTabReducer);
  const dispatch = useDispatch();
  const isLogged = useSelector(state => state.isLoggedReducer);
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArray = useSelector(state => state.filteringArraysReducer);

  const history = useHistory();

  const signOut = () => {
    localStorage.removeItem("token");
    dispatch(getUserAction({}));
    dispatch(isLoggedAction({}));
  };

  const oneTime = (() => {
    if (flag) {
      dispatch(
        updateArraysAction({
          tasksCounter: loggedUser.tasksCounter,
          todoArray: loggedUser.todoArray,
          inProgressArray: loggedUser.inProgressArray,
          completedTasksArray: loggedUser.completedTasksArray
        })
      );
      setFlag(false);
    }
  })();

  const getSearchInput = e => {
    e.preventDefault();
    if (e.target.value === "") {
      dispatch(
        filteringArraysAction([
          ...loggedUser.todoArray,
          ...loggedUser.inProgressArray,
          ...loggedUser.completedTasksArray
        ])
      );
    } else {
      setSearchInput(e.target.value);
      let searchInput = e.target.value;
      const filteredTasks = tasksArray.filter(task =>
        task.title.toUpperCase().includes(searchInput.toUpperCase())
      );
      dispatch(filteringArraysAction([...filteredTasks]));
    }
  };

  return (
    <Wrapper
      id="dashWrapper"
      onClick={e => {
        console.log(e.target.id);
        dropDownIsOpen && setDropDownIsOpen(false);
        if (e.target.id !== "searchInput" || e.target.id !== "searchButton") {
          // setSearchIsOpen(false);
        }
        // if (e.currentTarget.id === "dashWrapper")
        //   searchIsOpen && setSearchIsOpen(false);
      }}
    >
      {isLogged === false && <Redirect push to="/" />}
      <AppContainer>
        <TaskCreationWrapper
          id="taskWrapper"
          popupIsOpen={popupIsOpen}
          onClick={e => {
            e.target.id === "taskWrapper" && setPopupIsOpen(false);
          }}
        >
          <TaskCreationContainer>
            <TaskCreation setPopupIsOpen={setPopupIsOpen} />
          </TaskCreationContainer>
        </TaskCreationWrapper>

        <HeaderContainer>
          <Logo onClick={() => setDropDownIsOpen(!dropDownIsOpen)}>
            <MenuIndicator dropDownIsOpen={dropDownIsOpen}>
              <MenuIcon />
            </MenuIndicator>
            <LogoImg avatarImg={loggedUser.avatar} />
            <DropDownMenu dropDownIsOpen={dropDownIsOpen}>
              <WelcomeNote>{`Hi, ${loggedUser.userName}`}</WelcomeNote>
              <CreditsButton onClick={() => history.push("/credits")}>
                Credits
              </CreditsButton>
              <LogOutButton onClick={signOut}>Log Out</LogOutButton>
            </DropDownMenu>
          </Logo>
          <Header>
            Dead<span>l</span>ine
          </Header>
          <Search />
        </HeaderContainer>

        <TabsContainer>
          <TodoTab href="#todo" currentTab={currentTab}>
            To Do
          </TodoTab>
          <InProgressTab href="#progress" currentTab={currentTab}>
            In Progress
          </InProgressTab>
          <DoneTab href="#done" currentTab={currentTab}>
            Done
          </DoneTab>
        </TabsContainer>

        <TrackersContainer>
          <PageCarousel>
            <Todo />
            <InProgress />
            <Done />
          </PageCarousel>
          <AddTrackerButton onClick={() => setPopupIsOpen(true)}>
            +<span> l</span>
          </AddTrackerButton>
        </TrackersContainer>
      </AppContainer>
    </Wrapper>
  );
};

export default Dashbord;

const Wrapper = styled.div`
  ${flexbox()};
  height: 100%;
  width: 100%;
`;

const AppContainer = styled.div`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 100%;
  width: 100%;
  height: 100vh;
  max-width: 500px;
  position: relative;
`;

const TaskCreationWrapper = styled.div`
  ${flexbox()}
  width: 100%;
  height: 100%;
  position: absolute;
  z-index: 4;
  /* background: rgba(0, 0, 0, 0.8); */
  background: ${c.bgShyBlue};
  transition: 0.2s ease-out;
  transform: ${({ popupIsOpen }) => (popupIsOpen ? "scale(1)" : "scale(0)")};
`;

const TaskCreationContainer = styled.div`
  width: 95%;
  height: 70%;
  position: absolute;
  z-index: 5;

  @media (max-width: 450px) {
    height: 80%;
  }
`;

const HeaderContainer = styled.div`
  ${flexbox({ jc: "space-around" })};
  height: 70px;
  width: 100%;
`;

const Logo = styled.div`
  text-align: center;
  flex-basis: 10%;
  position: relative;
`;

const LogoImg = styled.div`
  ${flexbox()};
  box-shadow: -3px -3px 7px 0 rgba(242, 248, 250, 0.9),
    2px 2px 6px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
  opacity: 1;
  height: 45px;
  width: 45px;
  margin-left: 2px;
  border-radius: 50%;
  background: ${({ avatarImg }) => `url(${avatarImg})`};
  background-position: center;
  background-size: cover;

  :hover {
    cursor: pointer;
    filter: brightness(1.2);
  }
`;

const MenuIcon = styled(FaHamburger)`
  font-size: 14px;
`;

const MenuIndicator = styled.div`
  ${flexbox()};
  position: absolute;
  padding-left: 1px;
  z-index: 2;
  right: -2px;
  bottom: -6px;
  height: 20px;
  width: 20px;
  border-radius: 50%;
  background: white;

  :hover {
    cursor: pointer;
  }
`;

const DropDownMenu = styled.div`
  ${flexbox({ dir: "column" })}
  position: absolute;
  margin-top: 11px;
  z-index: 3;
  display: ${({ dropDownIsOpen }) => (dropDownIsOpen ? "block" : "none")};
  height: 200px;
  width: 200px;
  border: 3px solid ${c.bgSlateBlue};
  border-radius: 2%;

  :after {
    content: "";
    position: absolute;
    top: -11px;
    left: 14px;
    width: 0;
    height: 0;
    border-left: 8px solid transparent;
    border-right: 8px solid transparent;
    border-bottom: 8px solid ${c.bgSlateBlue};
  }
`;

const WelcomeNote = styled.div`
  ${flexbox()}
  flex-basis: 33.333%;
  height: 33.333%;
  width: 100%;
  background: ${c.bgSlateBlue};
  font-weight: bold;
  font-size: 1.1rem;
  color: white;
`;

const CreditsButton = styled(WelcomeNote)`
  background: white;
  color: ${c.darkBlueText};
  transition: 0.2s ease-out;

  :hover {
    background: ${c.bgfadeBlue};
    cursor: pointer;
  }
`;

const LogOutButton = styled(CreditsButton)`
  color: crimson;
`;

const Header = styled.h1`
  color: ${c.darkBlueText};
  flex-basis: 65%;
  font-size: 2rem;
  text-align: center;
  font-family: "Titillium Web", sans-serif;
  span {
    color: red;
  }
`;

const TabsContainer = styled.div`
  ${flexbox({ jc: "space-evenly" })};
  width: 100%;
  height: 55px;
  box-shadow: 0px 5px 5px 0px rgba(0, 0, 0, 0.15);
  position: relative;
  z-index: 1;
  font-weight: bold;
  font-size: 1.1rem;
`;

const TodoTab = styled.a`
  ${flexbox()};
  flex-basis: 33%;
  height: 100%;
  border-radius: 15px 15px 0 0;
  color: black;
  transition: 0.2s ease-out;
  text-decoration: none;
  opacity: ${({ currentTab }) =>
    currentTab === "currentTabTodo" ? "1" : "0.4"};
`;

const InProgressTab = styled(TodoTab)`
  opacity: ${({ currentTab }) =>
    currentTab === "currentTabProgress" ? "1" : "0.4"};
`;
const DoneTab = styled(TodoTab)`
  opacity: ${({ currentTab }) =>
    currentTab === "currentTabDone" ? "1" : "0.4"};
`;

const TrackersContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
`;

const PageCarousel = styled.div`
  height: 100%;
  width: 100%;
