import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";
import { useDispatch, useSelector } from "react-redux";

import { MdKeyboardArrowRight } from "react-icons/md";

import { filteringArraysAction } from "../../state/actions/filteredTasksAction";
import {
  toggleDeadlineAction,
  toggleUrgencyAction
} from "../../state/actions/toggleSortAction";

import TasksLister from "./TasksLister";

const SearchScreen = () => {
  const dispatch = useDispatch();

  const [searchInput, setSearchInput] = useState("");
  const [filteredArray, setFilteredArray] = useState([]);

  const loggedUser = useSelector(state => state.userReducer[0]);
  const searchIsOpen = useSelector(state => state.searchWindowReducer);
  const desktopView = useSelector(state => state.toggleViewReducer);
  const currentTab = useSelector(state => state.markTabReducer);
  const sortTasksBy = useSelector(state => state.sortTasksByReducer);
  // const filteredArray = useSelector(state => state.filteringArraysReducer);
  const tasksArrays = useSelector(state => state.updateArraysReducer);
  const toggleSort = useSelector(state => state.toggleSortReducer);

  useEffect(() => {
    dispatch(
      filteringArraysAction([
        ...tasksArrays.todoArray,
        ...tasksArrays.inProgressArray,
        ...tasksArrays.completedTasksArray
      ])
    );
    setFilteredArray([
      ...tasksArrays.todoArray,
      ...tasksArrays.inProgressArray,
      ...tasksArrays.completedTasksArray
    ]);
  }, []);

  const getSearchInput = e => {
    e.preventDefault();
    if (e.target.value === "") {
      dispatch(
        filteringArraysAction([
          ...tasksArrays.todoArray,
          ...tasksArrays.inProgressArray,
          ...tasksArrays.completedTasksArray
        ])
      );
      setFilteredArray([
        ...tasksArrays.todoArray,
        ...tasksArrays.inProgressArray,
        ...tasksArrays.completedTasksArray
      ]);
    } else {
      setSearchInput(e.target.value);
      let searchInput = e.target.value;
      dispatch(
        filteringArraysAction([
          ...tasksArrays.todoArray,
          ...tasksArrays.inProgressArray,
          ...tasksArrays.completedTasksArray
        ])
      );
      const filteringArrays = [
        ...tasksArrays.todoArray,
        ...tasksArrays.inProgressArray,
        ...tasksArrays.completedTasksArray
      ];

      const filteredTasks = filteringArrays.filter(task =>
        task.title.toUpperCase().includes(searchInput.toUpperCase())
      );

      setFilteredArray(filteredTasks);
    }
  };

  return (
    <SearchScreenBox searchIsOpen={searchIsOpen} desktopView={desktopView}>
      <SearchForm>
        <SearchInput placeholder="Search Deadline" onChange={getSearchInput} />
        <ToggleSortBox>
          <ToggleDeadline
            onClick={() => dispatch(toggleDeadlineAction(true))}
            toggleSort={toggleSort}
          ></ToggleDeadline>
          <ToggleUrgency
            onClick={() => dispatch(toggleUrgencyAction(false))}
            toggleSort={toggleSort}
          ></ToggleUrgency>
        </ToggleSortBox>
      </SearchForm>
      <TasksDisplay>
        {
          <TasksLister
            arrayToMap={filteredArray}
            firstButtonText={"firstButtonText"}
            secondButtonText={"secondButtonText"}
          />
        }
      </TasksDisplay>
    </SearchScreenBox>
  );
};

export default SearchScreen;

const SearchScreenBox = styled.div`
  ${flexbox()};
  display: ${({ searchIsOpen }) => (searchIsOpen ? "block" : "none")};
  width: 100%;
  height: 100%;
  height: calc(100% - 70px);
  background: ${c.gradientCyan};
  position: absolute;
  bottom: 0;
  z-index: 8;

  @media (min-width: 767px) {
    max-width: ${({ desktopView }) => (desktopView ? "1200px" : "500px")};
  }
`;

const SearchHeadSection = styled.div`
  ${flexbox()};
  height: 10%;
  width: 100%;
  font-size: 3rem;
  text-align: center;
  color: white;
  font-family: "Montserrat", sans-serif;
  font-weight: bold;
  letter-spacing: 3px;
`;

const SearchForm = styled.form`
  ${flexbox()}
  width: 100%;
  height: 20%;
  position: relative;
  flex-wrap: wrap;
`;

const SearchInput = styled.input`
  border: none;
  background: ${c.inputShyBlue};
  font-family: "Helvetica Neue", sans-serif;
  border-radius: 25px;
  padding-left: 15px;
  width: 350px;
  height: 50px;
  font-size: 1.1rem;
  /* width: 130px; */

  box-shadow: ${c.inputShadows};
`;

const ToggleSortBox = styled.div`
  display: block;
  ${flexbox()};
  flex-basis: 10%;
  min-width: 100px;

  background: ${c.elmShyBlue};
  border-radius: 5px;

  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);

  :hover {
    cursor: pointer;
  }

  @media (max-width: 767px) {
    display: none;
  }
`;

const ToggleDeadline = styled.div`
  ${flexbox()};
  font-size: 1.3rem;
  border-radius: 5px 0 0 5px;
  height: 45px;
  width: 50%;
  flex-basis: 50%;

  transition: 0.1s ease-out;
  background: ${({ toggleSort }) => (toggleSort ? "#ced9db" : "#d8e7eb")};
  box-shadow: ${({ toggleSort }) =>
    toggleSort
      ? "inset 2px 2px 4px -2px rgba(54, 73, 79, 1), inset -2px -2px 4px -2px rgba(242, 248, 250, 1)"
      : "2px 2px 4px -2px rgba(54, 73, 79, 1), -2px -2px 4px -2px rgba(242, 248, 250, 1)"};

  :hover {
    filter: brightness(1.1);
  }
`;

const ToggleUrgency = styled(ToggleDeadline)`
  border-radius: 0 5px 5px 0;

  background: ${({ toggleSort }) => (toggleSort ? "#d8e7eb" : "#ced9db")};

  box-shadow: ${({ toggleSort }) =>
    toggleSort
      ? "2px 2px 4px -2px rgba(54, 73, 79, 1), -2px -2px 4px -2px rgba(242, 248, 250, 1)"
      : "inset 2px 2px 4px -2px rgba(54, 73, 79, 1), inset -2px -2px 4px -2px rgba(242, 248, 250, 1)"};
`;

const TasksDisplay = styled.div`
  ${flexbox()};
  width: 100%;
  height: 80%;
  overflow-y: auto;
`;
