import React from "react";
import styled from "styled-components";

const Login = () => {
  return (
    <Wrapper>
      <Header>
        Tracker<span>.</span>
      </Header>
      <Container>
        <FormBox>
          <EmailInput placeholder="Email..." />
          <PasswordInput placeholder="Password..." />
          <LoginButton>Log In</LoginButton>
          <SignUpButton>Sign Up</SignUpButton>
          <GuestButton>Log In as Guest</GuestButton>
        </FormBox>
      </Container>
    </Wrapper>
  );
};

export default Login;

const Wrapper = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  flex-direction: column;

  color: #292f33;
`;

const Header = styled.div`
  flex-basis: 10%;
  font-size: 4rem;
  font-family: "Titillium Web", sans-serif;

  span {
    color: red;
    font-size: 5rem;
    font-family: "Montserrat", sans-serif;
  }
`;

const Container = styled.div`
  flex-basis: 65%;
  width: 80%;
  border-radius: 15px;
  box-shadow: 7px 7px 16px 0 rgba(242, 248, 250, 0.9),
    -7px -7px 16px 0 rgba(54, 73, 79, 0.25);
  padding: 20px;

  max-height: 375px;

  font-family: "Montserrat", sans-serif;

  display: flex;
  justify-content: center;
  align-items: center;
`;

const FormBox = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;

  width: 90%;
  height: 100%;
`;

const EmailInput = styled.input`
  width: 100%;
  height: 15%;
  border-radius: 5px;
  border: none;
  background: rgba(242, 248, 250, 0.55);
  box-shadow: inset 2px 2px 4px rgba(54, 73, 79, 0.25),
    inset -2px -2px 4px rgba(242, 248, 250, 0.9);
  padding-left: 10px;
  font-size: 0.8rem;
  opacity: 0.8;
`;
const PasswordInput = styled(EmailInput)`
  margin-bottom: 10px;
`;

const LoginButton = styled.button`
  width: 65%;
  height: 11%;
  border-radius: 5px;
  box-shadow: 3px 3px 6px 0 rgba(242, 248, 250, 0.85),
    -3px -3px 6px 0 rgba(54, 73, 79, 0.25);
  border: none;
  background: #dfe8eb;
  font-size: 0.7rem;
  margin-bottom: 5px;
  color: #333b3d;
`;
const SignUpButton = styled(LoginButton)``;
const GuestButton = styled(LoginButton)``;
