import React, { useState, useEffect } from "react";
import styled from "styled-components";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import Task from "../Task/Task";

import TasksLister from "../TasksLister";

//-------- Redux --------//
import { markProgress } from "../../../state/actions/markTabAction";
import { useSelector, useDispatch } from "react-redux";
import { useInView } from "react-intersection-observer";

const InProgress = () => {
  const [refreshCounter, setRefreshCounter] = useState(0);
  const [ref, inView] = useInView({ threshold: 0.5 });
  const [arrayToMap, setArrayToMap] = useState([]);
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArrays = useSelector(state => state.updateArraysReducer);
  const filteringArrays = useSelector(state => state.filteringArraysReducer);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatchCurrentTab();
  }, [inView]);

  const dispatchCurrentTab = () => {
    if (inView) {
      dispatch(markProgress());
    }
  };

  useEffect(() => {
    findArrayToMap();
  }, [filteringArrays]);

  const findArrayToMap = () => {
    if (filteringArrays.length >= 1) {
      console.log("filteringArrays", filteringArrays[0].inProgressArray);
      setArrayToMap(filteringArrays[0].inProgressArray);
    } else setArrayToMap(tasksArrays.inProgressArray);
  };

  return (
    <Wrapper id="progress" ref={ref}>
      <TasksLister
        arrayToMap={arrayToMap}
        firstButtonText={"Forward to Done"}
        secondButtonText={"Return to Todo"}
      />
    </Wrapper>
  );
};

export default InProgress;

const Wrapper = styled.div`
  /* without vertical-align the inline-blocks moves down */
  vertical-align: top;
  scroll-snap-align: center;
  scroll-snap-stop: always;

  display: inline-block;
  width: 100%;
  height: 100%;

  background: ${c.gradientGreen};
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.15),
    inset -2px -2px 8px rgba(242, 248, 250, 0.2);

  @media (min-width: 700px) {
    width: 33.333%;
  }
`;

