import React, { useState } from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import { Redirect, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";

//-------Components-------//
import Todo from "./Todo/Todo";
import InProgress from "./InProgress/InProgress";
import Done from "./Done/Done";
import TaskCreation from "./TaskCreation";

//-------Actions--------//
import { getUserAction } from "../../state/actions/getUserAction";
import { isLoggedAction } from "../../state/actions/isLoggedAction";
import { updateArraysAction } from "../../state/actions/updateArraysAction";

const Dashbord = () => {
  const [popupIsOpen, setPopupIsOpen] = useState(false);

  const dispatch = useDispatch();
  const isLogged = useSelector(state => state.isLoggedReducer);
  const loggedUser = useSelector(state => state.userReducer[0]);
  const signOut = () => {
    dispatch(getUserAction({}));
    dispatch(isLoggedAction({}));
  };

  dispatch(
    updateArraysAction({
      todoArray: loggedUser.todoArray,
      inProgressArray: loggedUser.inProgressArray,
      completedTasksArray: loggedUser.completedTasksArray
    })
  );

  return (
    <Wrapper>
      {/* {isLogged === false && <Redirect push to="/" />} */}
      <AppContainer>
        <TaskCreationWrapper
          id="close"
          popupIsOpen={popupIsOpen}
          onClick={e => e.target.id === "close" && setPopupIsOpen(false)}
        >
          <TaskCreationContainer>
            <TaskCreation />
          </TaskCreationContainer>
        </TaskCreationWrapper>

        <HeaderContainer>
          <LogOutButton onClick={signOut}>Log Out</LogOutButton>
          <Header>
            Tracker<span>.</span>
          </Header>
          <Logo>{/* <div>{loggedUser.avatar}</div> */}</Logo>
        </HeaderContainer>

        <TabsContainer>
          <TodoTab href="#todo">To Do</TodoTab>
          <InProgressTab href="#progress">In Progress</InProgressTab>
          <DoneTab href="#done">Done</DoneTab>
        </TabsContainer>

        <TrackersContainer>
          <PageCarousel>
            <Todo />
            <InProgress />
            <Done />
          </PageCarousel>
          <AddTrackerButton onClick={() => setPopupIsOpen(true)} />
        </TrackersContainer>
      </AppContainer>
    </Wrapper>
  );
};

export default Dashbord;

const Wrapper = styled.div`
  ${flexbox()};
  height: 100%;
  width: 100%;
`;

const AppContainer = styled.div`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 100%;
  width: 100%;
  height: 100vh;
  max-width: 500px;
  position: relative;
`;

const TaskCreationWrapper = styled.div`
  ${flexbox()}
  width: 100%;
  height: 100%;
  position: absolute;
  z-index: 1;
  background: rgba(0, 0, 0, 0.8);
  transition: 0.2s ease-out;
  transform: ${({ popupIsOpen }) => (popupIsOpen ? "scale(1)" : "scale(0)")};
`;

const TaskCreationContainer = styled.div`
  width: 95%;
  height: 50%;
  position: absolute;
  z-index: 2;
`;

const HeaderContainer = styled.div`
  ${flexbox({ jc: "space-around" })};
  height: 50px;
  width: 100%;
`;

const LogOutButton = styled.button`
  flex-basis: 10%;
`;

const Header = styled.h1`
  color: ${c.darkBlueText};
  flex-basis: 70%;
  font-size: 2rem;
  text-align: center;
  font-family: "Titillium Web", sans-serif;
  span {
    color: red;
    font-family: "Montserrat", sans-serif;
  }
`;

const Logo = styled.h1`
  text-align: center;
  flex-basis: 10%;

  div {
    ${flexbox()};
    height: 30px;
    width: 30px;
    border-radius: 50%;
  }
`;

const TabsContainer = styled.div`
  ${flexbox({ jc: "space-evenly" })};
  width: 100%;
  height: 75px;
`;

const TodoTab = styled.a`
  ${flexbox()};
  flex-basis: 25%;
  height: 100%;
  border-radius: 15px 15px 0 0;
  color: black;
  text-decoration: none;
`;

const InProgressTab = styled(TodoTab)``;
const DoneTab = styled(TodoTab)``;

const TrackersContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  background: lightyellow;
`;

const PageCarousel = styled.div`
  height: 100%;
  width: 100%;
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;
  scroll-snap-type: x mandatory;
  scroll-behavior: smooth;
`;

const AddTrackerButton = styled.div`
  position: absolute;
  right: 10px;
  bottom: 10px;
  border: 1px solid blue;
  border-radius: 50%;
  height: 50px;
  width: 50px;
`;
