import React, { useState, useEffect } from "react";
import styled from "styled-components";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import Task from "../Task";

//-------- Redux --------//
import { markProgress } from "../../../state/actions/markTabAction";
import { useSelector, useDispatch } from "react-redux";
import { useInView } from "react-intersection-observer";

const InProgress = () => {
  const [refreshCounter, setRefreshCounter] = useState(0);
  const [ref, inView] = useInView({ threshold: 1 });
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArrays = useSelector(state => state.updateArraysReducer);
  const dispatch = useDispatch();

  useEffect(() => {
    todoTasks();
    console.log("tasksArrays", tasksArrays);
  }, [refreshCounter]);

  useEffect(() => {
    dispatchCurrentTab();
  }, [inView]);

  const dispatchCurrentTab = () => {
    if (inView) {
      dispatch(markProgress());
    }
  };

  const todoTasks = () => {
    return tasksArrays.inProgressArray.map((task, index) => {
      return (
        <Task
          id={task.id}
          key={index}
          title={task.title}
          description={task.description}
          deadline={task.deadline}
          colorway={task.colorway}
          status={task.status}
          refreshCounter={refreshCounter}
          setRefreshCounter={setRefreshCounter}
          firstButtonText={"Forward to Done"}
          secondButtonText={"Return to Todo"}
        />
      );
    });
  };

  return (
    <Wrapper id="progress" ref={ref}>
      <TasksList>{todoTasks()}</TasksList>
    </Wrapper>
  );
};

export default InProgress;

const Wrapper = styled.div`
  /* without vertical-align the inline-blocks moves down */
  vertical-align: top;
  scroll-snap-align: center;
  scroll-snap-stop: always;

  display: inline-block;
  width: 100%;
  height: 100%;

  background: ${c.gradientGreen};
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.15),
    inset -2px -2px 8px rgba(242, 248, 250, 0.2);
`;

const TasksList = styled.div`
  /* ${flexbox({ dir: "column", jc: "flex-start" })}; */
  height: 100%;
  width: 100%;
  overflow-y: auto;
`;
