import { UPDATEARRAYS } from "../actions/updateArraysAction";

const updateTasksArraysReducer = (state = {}, action) => {
  switch (action.type) {
    case UPDATEARRAYS:
      return { ...state, ...action.payload };
      break;

    default:
      return state;
      break;
  }
};

export default updateTasksArraysReducer;
