import React, { useState } from "react";
import styled from "styled-components";
import { Redirect } from "react-router-dom";
import * as c from "../../style/colors";

import fetchAPI from "../../service/services";

import { flexbox, borderShadowsRadius, buttonStyle } from "../../style/mixins";

const SignUp = () => {
  const [passwordCompare, setPasswordCompare] = useState("");
  const [redirect, setRedirect] = useState(false);
  const [matchError, setMatchError] = useState("");
  const [avatar, setAvatar] = useState("");

  const matchCheck = e => {
    if (e.target.value !== passwordCompare) {
      setMatchError("#ff5757");
    } else if (e.target.value === passwordCompare) {
      setMatchError("#87e882");
    }
    if (e.target.value === "") {
      setMatchError("");
    }
  };

  const currentdate = new Date();
  const weekdayds = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  console.log(weekdayds[3]);
  console.log(currentdate.getDay());

  const changeAvatar = e => {
    setAvatar(`https://api.adorable.io/avatars/${e.target.value}`);
  };

  const sendForm = async e => {
    e.preventDefault();
    const userName = e.target[0].value;
    const userEmail = e.target[1].value;
    const userPassword = e.target[2].value;
    const confirmPassword = e.target[3].value;

    if (userPassword != confirmPassword) {
      return console.log("Passwords do not match");
    } else {
      try {
        const theCurrentdate = new Date();
        const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
        const monthNames = [
          "Jan",
          "Feb",
          "Mar",
          "Apr",
          "May",
          "Jun",
          "Jul",
          "Aug",
          "Sep",
          "Oct",
          "Nov",
          "Dec"
        ];
        const userSignData = {
          userName,
          email: userEmail,
          password: userPassword,
          avatar: `https://api.adorable.io/avatars/${userName}`,
          todoArray: [
            {
              id: -1,
              title: "Wellcome",
              // description,
              dayText: weekdays[theCurrentdate.getDay()],
              monthText: monthNames[theCurrentdate.getMonth()],
              dayNumber: theCurrentdate.getDate(),
              year: theCurrentdate.getFullYear(),
              time:
                theCurrentdate.getHours() + ":" + theCurrentdate.getMinutes(),
              colorway: "blue",
              status: "todoArray",
              urgency: "high",
              dateAndTimeSum: 1
            },
            {
              id: -1,
              title: "Wellcome",
              // description,
              dayText: weekdays[theCurrentdate.getDay() + 1],
              monthText: monthNames[theCurrentdate.getMonth() + 1],
              dayNumber: theCurrentdate.getDate() + 1,
              year: theCurrentdate.getFullYear() + 1,
              time:
                theCurrentdate.getHours() +
                1 +
                ":" +
                theCurrentdate.getMinutes(),
              colorway: "turquoise",
              status: "todoArray",
              urgency: "low",
              dateAndTimeSum: 1
            }
          ],
          inProgressArray: [],
          completedTasksArray: []
        };

        const jsonUserData = await fetchAPI({
          urlString: "createUser",
          method: "newUserInDB",
          body: userSignData
        });

        console.log("userSignData", userSignData);

        // const options = {
        //   method: "POST",
        //   headers: {
        //     "Content-Type": "application/json"
        //   },
        //   body: JSON.stringify(userSignData)
        // };
        // await fetch(
        //   "https://tracker-database.youngwebdevs.now.sh/login",
        //   options
        // );
        setRedirect(true);
      } catch (error) {
        console.log("error: ", error);
      }
    }
  };

  return (
    <Wrapper>
      {redirect && <Redirect push to="/" />}
      <Header>
        Sign Up <span>:)</span>
      </Header>
      <SignUpContainer onSubmit={sendForm}>
        <AvatarDisplay avatarImg={avatar} />
        <UserName
          type="text"
          placeholder="Choose User Name..."
          onChange={changeAvatar}
          minLength="4"
        />
        <UserEmail type="email" placeholder="Choose Email..." minLength="2" />
        <UserPassword
          onChange={e => setPasswordCompare(e.target.value)}
          type="password"
          placeholder="Choose Password..."
          minLength="4"
        />
        <ConfirmPassword
          onChange={matchCheck}
          matchError={matchError}
          type="password"
          placeholder="Confirm Password..."
          minLength="4"
        />
        <SignUpButton type="submit">Submit</SignUpButton>
      </SignUpContainer>
    </Wrapper>
  );
};

export default SignUp;

const Wrapper = styled.div`
  ${flexbox({ dir: "column", jc: "space-around" })};
  height: 100%;
  width: 100%;
`;

const Header = styled.div`
  flex-basis: 10%;
  font-size: 4rem;
  font-family: "Titillium Web", sans-serif;

  span {
    color: red;
    font-size: 5rem;
    font-family: "Montserrat", sans-serif;
  }
`;

const SignUpContainer = styled.form`
  ${flexbox({ dir: "column", jc: "center" })};
  ${borderShadowsRadius};
  flex-basis: 65%;
  width: 80%;
  padding: 20px;

  max-height: 475px;
  max-width: 400px;
`;

const AvatarDisplay = styled.div`
  height: 50px;
  width: 50px;
  border-radius: 50%;
  font-size: 2rem;
  ${flexbox()}
  text-align: center;
  margin-bottom: 15px;
  pointer-events: none;
  background: ${({ avatarImg }) => `url(${avatarImg})`};
  background-position: center;
  background-size: cover;
  box-shadow: -2px -2px 6px 0 rgba(242, 248, 250, 0.9),
    2px 2px 6px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
`;

const UserName = styled.input`
  box-shadow: ${c.inputShadows};
  background: ${c.inputShyBlue};
  color: ${c.darkBlueText};
  margin: 10px;
  width: 100%;
  height: 10%;
  border-radius: 5px;
  border: none;
  padding-left: 10px;
  font-size: 0.8rem;

  opacity: 0.7;
  transition: 0.3s ease-out;
  outline: none;

  &:hover,
  &:focus {
    opacity: 1;
  }
`;

const UserEmail = styled(UserName)``;
const UserPassword = styled(UserName)``;
const ConfirmPassword = styled(UserName)`
  background-color: ${({ matchError }) => matchError};
`;
const SignUpButton = styled.button`
  margin-top: 15px;
  ${buttonStyle}

  background: ${c.elmShyBlue};
  color: ${c.darkBlueText};
  &:hover,
  &:focus {
    opacity: 0.9;
    cursor: pointer;
    background: ${c.lightBlue};
  }
`;
