import React from "react";
import styled from "styled-components";
import { flexbox } from "../../../style/mixins";

const TaskStatusSelect = ({ status, setStatus }) => {
  return (
    <TaskStatusSelectBox>
      <StatusOption onClick={() => setStatus("todo")} status={status}>
        ToDo
      </StatusOption>
      <StatusOption onClick={() => setStatus("inProgress")} status={status}>
        In Progress
      </StatusOption>
      <StatusOption onClick={() => setStatus("done")} status={status}>
        Done
      </StatusOption>
    </TaskStatusSelectBox>
  );
};

export default TaskStatusSelect;

const TaskStatusSelectBox = styled.div`
  ${flexbox()}
  width: 50%;
  height: 50px;
  border-radius: 5px;
  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);
`;

const StatusOption = styled.div`
  ${flexbox()}
  flex-basis: 33.333%;
  height: 50px;

  box-shadow: ${({ status }) =>
    status === "low"
      ? "inset 2px 2px 4px 1px rgba(54, 73, 79, 0.50), inset -2px -2px 4px 1px rgba(242, 248, 250, 0.50)"
      : ""};

  :hover {
    cursor: pointer;
    font-weight: bold;
  }
`;
