    // case "post":
    //   options = post;
    //   break;
    default:
      break;
  }

  const urls = {
    logWithToken: `https://tracker-database.youngwebdevs.now.sh/login/loggeduser/`,
    createUser: `https://tracker-database.youngwebdevs.now.sh/login/loggeduser/`,
    // get: `https://tracker-database.youngwebdevs.now.sh/login/userfromdb/?userName=${paramOne}&password=${paramTwo}`,
    put: `https://tracker-database.youngwebdevs.now.sh/dashboard/updatingarrays`
  };

  console.log("urls[urlString]", urls[urlString]);
  console.log("options", options);
  const result = await fetch(urls[urlString], options);
  if (method === "getToken") {
    localStorage.setItem("token", result.headers.get("auth-token"));
  }
  const resultJSON = await result.json();
  return resultJSON;
};

export default fetchAPI;
