import React, { useState } from "react";
import styled from "styled-components";
import Task from "./Task/Task";
import { flexbox } from "../../style/mixins";
import { useSelector } from "react-redux";

const TasksLister = ({ arrayToMap, firstButtonText, secondButtonText }) => {
  const sortTasksBy = useSelector(state => state.sortTasksByReducer);

  //<--IF (sortTaskBy === false) THE TASKS SORTED BY DEADLINE-->//
  if (!sortTaskBy) {
    const compare = (a, b) => {
      const timeA = a.dateAndTimeSum;
      const timeB = b.dateAndTimeSum;

      let comparison = 0;
      if (timeA > timeB) {
        comparison = 1;
      } else if (timeA < timeB) {
        comparison = -1;
      }
      return comparison;
    };
    arrayToMap.sort(compare);
  }
  //<--IF (sortTaskBy === true) THE TASKS SORTED BY URGENCY-->//
  else {
    const compare = (a, b) => {
      // console.log("sort by urgency");
      // console.log("a.urgency", a.urgency);
      // console.log("b.urgency", b.urgency);

      const urgencyNumberA = () => {
        switch (a.urgency) {
          case "low":
            return 0;
            break;
          case "regular":
            return 1;
            break;
          case "high":
            return 2;
            break;
        }
      };

      const urgencyNumberB = () => {
        switch (b.urgency) {
          case "low":
            return 0;
            break;
          case "regular":
            return 1;
            break;
          case "high":
            return 2;
            break;
        }
      };

      const urgencyA = urgencyNumberA();
      const urgencyB = urgencyNumberB();

      // console.log("urgencyA", urgencyA);
      // console.log("urgencyB", urgencyB);

      let comparison = 0;
      if (urgencyA < urgencyB) {
        comparison = 1;
      } else if (urgencyA > urgencyB) {
        comparison = -1;
      }
      // console.log("comparison", comparison);
      return comparison;
    };

    const sortedArray = arrayToMap.sort(compare);
    // console.log("sortedArray", sortedArray);
  }
  // <----------------------------->

  const tasksMap = () => {
    return arrayToMap.map((task, index) => {
      return (
        <Task
          id={task.id}
          key={index}
          dayText={task.dayText}
          monthText={task.monthText}
          dayNumber={task.dayNumber}
          dateAndTimeSum={task.dateAndTimeSum}
          time={task.time}
          year={task.year}
          title={task.title}
          description={task.description}
          deadline={task.deadline}
          colorway={task.colorway}
          urgency={task.urgency}
          status={task.status}
        />
      );
    });
  };

  return <TasksList>{tasksMap()}</TasksList>;
};

export default TasksLister;

const TasksList = styled.div`
  ${flexbox({ jc: "flex-start", ai: "flex-start" })}
  align-content: flex-start;
  padding: 20px;
  flex-wrap: wrap;
  height: 100%;
  width: 100%;
  overflow-y: auto;
`;
