import React from "react";
import styled from "styled-components";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import { ReactComponent as FireSVG } from "../../../assets/SVG/fire.svg";
import { ReactComponent as AlertSVG } from "../../../assets/SVG/warning-sign.svg";

const HeaderArea = ({ urgency, title, searchIsOpen, status }) => {
  return (
    <HeaderAreaBox>
      <UrgencyIconsContainer>
        <UrgencyIconBox>
          {urgency === "regular" && <AlertICON />}
          {urgency === "high" && <FireSVG />}
        </UrgencyIconBox>
      </UrgencyIconsContainer>
      <Title>{title}</Title>
      {searchIsOpen && (
        <StatusDisplay>{`Currently in: ${status}`}</StatusDisplay>
      )}
    </HeaderAreaBox>
  );
};

export default HeaderArea;

const HeaderAreaBox = styled.div`
  ${flexbox({ jc: "flex-start" })};
  flex-basis: 30%;
  height: 30%;
  width: 100%;
  border-radius: 10px;
  position: relative;
`;

const UrgencyIconsContainer = styled.div`
  height: 100%;
  width: 3%;
  flex-basis: 3%;
  position: relative;
`;

const UrgencyIconBox = styled.div`
  position: absolute;
  height: 32px;
  width: 32px;
  left: -16px;
  top: -16px;
`;

const AlertICON = styled(AlertSVG)`
  fill: firebrick;
`;

const Title = styled.div`
  ${flexbox({ jc: "flex-start" })}
  height: 100%;
  width: calc(97% -50px);
  flex-basis: calc(97% -50px);
  font-size: 1.5rem;
  font-weight: bold;
  color: ${c.darkText};

  overflow-x: auto;
`;

const StatusDisplay = styled.div`
  position: absolute;
  top: 5px;
  left: 0;
  width: 100%;
  color: ${c.darkText};
  height: 10px;
  font-size: 0.7rem;
  padding-left: 15px;
`;
