import React from "react";
import styled from "styled-components";

import { flexbox } from "../../style/mixins";

const TaskCreation = () => {
  return (
    <Wrapper>
      <Header>New Task</Header>
      <TaskForm id="taskCreate">
        <TitleInput type="text" placeholder="Task Title..." />
        <DescrptionInput type="text" placeholder="Task Descrption..." />
        <DeadlineInput type="datetime-local" />
        <TaskColorPiker name="type" form="taskCreate">
          <ColorOption value="ToDo">red</ColorOption>
          <TypeOption value="InProgress">blue</TypeOption>
          <TypeOption value="Done">green</TypeOption>
          <TypeOption value="Done">black</TypeOption>
          <TypeOption value="Done">orange</TypeOption>
        </TaskColorPiker>
        <SubmitButton type="submit">Add</SubmitButton>
      </TaskForm>
      <TaskTypeSelect name="type" form="taskCreate">
        <TypeOption value="ToDo">ToDo</TypeOption>
        <TypeOption value="InProgress">InProgress</TypeOption>
        <TypeOption value="Done">Done</TypeOption>
      </TaskTypeSelect>
    </Wrapper>
  );
};

export default TaskCreation;

const Wrapper = styled.div`
  border: 1px solid black;
  width: 100%;
  z-index: 1;
`;
const Header = styled.h1``;
const TaskForm = styled.div`
  ${flexbox()}
  flex-wrap: wrap;
`;
const TitleInput = styled.input``;
const DescrptionInput = styled.input``;
const DeadlineInput = styled.input``;
const TaskColorPiker = styled.select``;
const ColorOption = styled.option`
  background-color: red;
`;
const SubmitButton = styled.button``;
const TaskTypeSelect = styled.select``;
const TypeOption = styled.option``;
