import { UPDATEARRAYS } from "../actions/tasksArraysAction";

const updateTasksArraysReducer = (state = null, action) => {
  switch (action.type) {
    case UPDATEARRAYS:
      return action.payload;
      break;

    default:
      break;
  }
};

export default updateTasksArraysReducer;
