import React from "react";
import styled from "styled-components";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import { TiArrowLoop } from "react-icons/ti";
import { ReactComponent as FireSVG } from "../../../assets/SVG/fire.svg";
import { ReactComponent as AlertSVG } from "../../../assets/SVG/warning-sign.svg";

const HeaderArea = ({
  urgency,
  title,
  searchIsOpen,
  status,
  cardIsFlipped,
  setCardIsFlipped
}) => {
  return (
    <HeaderAreaBox>
      <UrgencyIconsContainer>
        <UrgencyIconBox>
          {urgency === "regular" && <AlertICON />}
          {urgency === "high" && <FireSVG />}
        </UrgencyIconBox>
      </UrgencyIconsContainer>
      <TitleBox>
        {searchIsOpen && (
          <StatusDisplay>{`Currently in: ${status}`}</StatusDisplay>
        )}
        <Title>{title}</Title>
      </TitleBox>
      <OptionsButtonBox onClick={() => setCardIsFlipped(!cardIsFlipped)}>
        <StyledArrowIcon />
      </OptionsButtonBox>
    </HeaderAreaBox>
  );
};

export default HeaderArea;

const HeaderAreaBox = styled.div`
  ${flexbox({ jc: "space-between" })};
  flex-basis: 25%;
  height: 25%;
  width: 100%;
  border-radius: 10px;
  position: relative;
`;

const UrgencyIconsContainer = styled.div`
  height: 100%;
  width: 3%;
  flex-basis: 3%;
  position: relative;
`;

const UrgencyIconBox = styled.div`
  position: absolute;
  height: 32px;
  width: 32px;
  left: -16px;
  top: -16px;
`;

const AlertICON = styled(AlertSVG)`
  fill: firebrick;
`;

const TitleBox = styled.div`
  ${flexbox({ dir: "column" })}
  height: 100%;
  width: calc(97% -50px);
  flex-basis: calc(97% -50px);
`;

const StatusDisplay = styled.div`
  flex-basis: 20%;
  height: 20%;
  width: 100%;
  color: ${c.darkText};
  height: 10px;
  font-size: 0.7rem;
  padding-left: 15px;
`;

const Title = styled.div`
  ${flexbox({ jc: "flex-start" })}
  flex-basis: 80%;
  height: 80%;
  width: 100%;
  font-size: 1.5rem;
  font-weight: bold;
  color: ${c.darkText};
  border: 2px solid blue;

  overflow-x: auto;
`;

const StyledArrowIcon = styled(TiArrowLoop)`
  transition: 2s infinite;

  :hover {
    filter: drop-shadow(0 0 5px gray);
  }
`;

const OptionsButtonBox = styled.div`
  border: 2px solid red;
  ${flexbox()};
  z-index: 1;
  height: 100%;
  width: 50px;
  font-size: 1.4rem;
  /* transform: rotateY(180deg) rotateX(180deg); */
  border-radius: 10px;
  background: white;
  transition: 0.3s ease-in-out;

  :hover {
    cursor: pointer;
  }
`;
