import React, { useReducer } from "react";
import styled from "styled-components";
import GlobalStyle from "../style/globalStyle";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { useSelector } from "react-redux";
import * as c from "../style/colors";
import isLoggedReducer from "../state/reducers/isLoggedReducer";

//-------Components-------//
import Login from "../view/Login/Login.jsx";
import SignUp from "../view/SignUp/SignUp.jsx";
import Dashbord from "../view/Dashbord/Dashboard.jsx";

function App() {
  const isLogged = useSelector(state => state.isLoggedReducer);
  // const [logged, dispatchLogged] = useReducer(isLoggedReducer);

  return (
    <Router>
      <Wrapper>
        <Switch>
          <Route exact path="/" component={Login} />
          <Route path="/signup" component={SignUp} />
          <Route path="/dashboard">{isLogged ? <Dashbord /> : <Login />}</Route>
        </Switch>
        <GlobalStyle />
      </Wrapper>
    </Router>
  );
}

export default App;

const Wrapper = styled.div`
  height: 100vh;
  width: 100vw;
  background: ${c.shyBlue};
  font-family: "Montserrat", sans-serif;
`;
