import React, { useState } from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import { Redirect, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { flexbox } from "../../style/mixins";

//-------Components-------//
import Todo from "./Todo/Todo";
import InProgress from "./InProgress/InProgress";
import Done from "./Done/Done";

//-------Actions--------//
import { importUserAction } from "../../state/actions/importUserAction";
import { isLoggedAction } from "../../state/actions/isLoggedAction";

const Dashbord = () => {
  const [offset, setOffset] = useState(0);

  const dispatch = useDispatch();
  const isLogged = useSelector(state => state.isLoggedReducer);
  const loggedUser = useSelector(state => state.userReducer[0]);
  const signOut = () => {
    dispatch(importUserAction({}));
    dispatch(isLoggedAction({}));
  };

  return (
    <Wrapper>
      {/* {isLogged === false && <Redirect push to="/" />} */}
      <AppContainer>
        <HeaderContainer>
          <LogOutButton onClick={signOut}>Log Out</LogOutButton>
          <Header>Tracker</Header>
          <Logo>{/* <div>{loggedUser.avatar}</div> */}</Logo>
        </HeaderContainer>

        <TabsContainer>
          <TodoTab href="#todo">To Do</TodoTab>
          <InProgressTab href="#progress">In Progress</InProgressTab>
          <DoneTab href="#done">Done</DoneTab>
        </TabsContainer>

        <TrackersContainer>
          <PageCarousel>
            <Todo />
            <InProgress />
            <Done />
          </PageCarousel>
          <AddTracker />
        </TrackersContainer>
      </AppContainer>
    </Wrapper>
  );
};

export default Dashbord;

const Wrapper = styled.div`
  ${flexbox()};
  height: 100%;
  width: 100%;
  scroll-behavior: smooth;
`;

const AppContainer = styled.div`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 100%;
  width: 100%;
  height: 100vh;
  max-width: 500px;
  border: 1px solid red;
`;

const HeaderContainer = styled.div`
  ${flexbox({ jc: "space-around" })};
  height: 50px;
  width: 100%;
  border: 1px solid red;
`;

const LogOutButton = styled.button`
  flex-basis: 10%;
`;

const Header = styled.h1`
  flex-basis: 70%;
  font-size: 2rem;
  text-align: center;
`;

const Logo = styled.h1`
  text-align: center;
  flex-basis: 10%;
  color: black;

  div {
    ${flexbox()};
    height: 30px;
    width: 30px;
    border-radius: 50%;
    border: 1px solid green;
  }
`;

const TabsContainer = styled.div`
  ${flexbox({ jc: "space-evenly" })};
  width: 100%;
  height: 75px;
  border: 1px solid blue;
`;

const TodoTab = styled.a`
  ${flexbox()};
  flex-basis: 25%;
  height: 100%;
  border: 2px solid green;
  border-radius: 15px 15px 0 0;
`;

const InProgressTab = styled(TodoTab)``;
const DoneTab = styled(TodoTab)``;

const TrackersContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  background: lightyellow;
`;

const PageCarousel = styled.div`
  height: 100%;
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;
  scroll-snap-type: x mandatory;
`;

const AddTracker = styled.div`
  position: absolute;
  right: 10px;
  bottom: 10px;
  border: 1px solid blue;
  border-radius: 50%;
  height: 50px;
  width: 50px;
`;
