import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";
import { useDispatch, useSelector } from "react-redux";

import { MdKeyboardArrowRight } from "react-icons/md";

import { filteringArraysAction } from "../../state/actions/filteredTasksAction";
import { sortTasksAction } from "../../state/actions/sortTasksAction";

import TasksLister from "./TasksLister";

const SearchScreen = () => {
  const dispatch = useDispatch();

  const [searchInput, setSearchInput] = useState("");
  const [filteredArray, setFilteredArray] = useState([]);

  const loggedUser = useSelector(state => state.userReducer[0]);
  const searchIsOpen = useSelector(state => state.searchWindowReducer);
  const desktopView = useSelector(state => state.toggleViewReducer);
  const currentTab = useSelector(state => state.markTabReducer);
  const sortTasksBy = useSelector(state => state.sortTasksByReducer);
  // const filteredArray = useSelector(state => state.filteringArraysReducer);
  const tasksArrays = useSelector(state => state.updateArraysReducer);

  useEffect(() => {
    dispatch(
      filteringArraysAction([
        ...tasksArrays.todoArray,
        ...tasksArrays.inProgressArray,
        ...tasksArrays.completedTasksArray
      ])
    );
    setFilteredArray([
      ...tasksArrays.todoArray,
      ...tasksArrays.inProgressArray,
      ...tasksArrays.completedTasksArray
    ]);
  }, []);

  const getSearchInput = e => {
    e.preventDefault();
    if (e.target.value === "") {
      dispatch(
        filteringArraysAction([
          ...tasksArrays.todoArray,
          ...tasksArrays.inProgressArray,
          ...tasksArrays.completedTasksArray
        ])
      );
      setFilteredArray([
        ...tasksArrays.todoArray,
        ...tasksArrays.inProgressArray,
        ...tasksArrays.completedTasksArray
      ]);
    } else {
      setSearchInput(e.target.value);
      let searchInput = e.target.value;
      dispatch(
        filteringArraysAction([
          ...tasksArrays.todoArray,
          ...tasksArrays.inProgressArray,
          ...tasksArrays.completedTasksArray
        ])
      );
      const filteringArrays = [
        ...tasksArrays.todoArray,
        ...tasksArrays.inProgressArray,
        ...tasksArrays.completedTasksArray
      ];

      const filteredTasks = filteringArrays.filter(task =>
        task.title.toUpperCase().includes(searchInput.toUpperCase())
      );

      setFilteredArray(filteredTasks);
    }
  };

  return (
    <SearchScreenBox searchIsOpen={searchIsOpen} desktopView={desktopView}>
      <SearchBar>
        <SearchHeadSection>Search</SearchHeadSection>
        <SearchBodySection>
          <SearchForm>
            <SearchInput onChange={getSearchInput} id="searchInput" />
            <SearchButtonSubmit />
            <SelectSortBox>
              <SortLabel>{`Order By: `}</SortLabel>
              <SortSelect
                onChange={() => dispatch(sortTasksAction(!sortTasksBy))}
              >
                <SortOption>Deadline</SortOption>
                <SortOption>Urgency</SortOption>
              </SortSelect>
            </SelectSortBox>
          </SearchForm>
          <TasksDisplay>
            {
              <TasksLister
                arrayToMap={filteredArray}
                firstButtonText={"firstButtonText"}
                secondButtonText={"secondButtonText"}
              />
            }
          </TasksDisplay>
        </SearchBodySection>
      </SearchBar>
    </SearchScreenBox>
  );
};

export default SearchScreen;

const SearchScreenBox = styled.div`
  ${flexbox()};
  display: ${({ searchIsOpen }) => (searchIsOpen ? "block" : "none")};
  width: 100%;
  height: 100%;
  height: calc(100% - 70px);
  background: linear-gradient(to bottom, #bbd2c5, #536976);
  position: absolute;
  bottom: 0;
  z-index: 8;

  @media (min-width: 767px) {
    max-width: ${({ desktopView }) => (desktopView ? "1200px" : "500px")};
  }
`;

const SearchHeadSection = styled.div`
  ${flexbox()};
  height: 10%;
  width: 100%;
  font-size: 3rem;
  text-align: center;
  color: white;
  font-family: "Montserrat", sans-serif;
  font-weight: bold;
  letter-spacing: 3px;
`;

const SearchBar = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
`;

const SearchBodySection = styled.div`
  height: 90%;
  width: 100%;
`;

const SearchForm = styled.form`
  ${flexbox()}
  width: 100%;
  height: 10%;
  position: relative;
  flex-wrap: wrap;
`;

const SearchInput = styled.input`
  border: 1px solid ${c.bgSlateBlue};
  width: 250px;
  font-size: 1.1rem;
  /* width: 130px; */
`;

const SearchButtonSubmit = styled(MdKeyboardArrowRight)`
  position: relative;
  right: 17px;
  /* top: 3px; */
  background-color: white;
  :hover {
    cursor: pointer;
  }
`;

const SelectSortBox = styled.div`
  /* border: 1px solid black; */
  ${flexbox()}
  width: 100%;
  /* padding-top: 5px; */
  font-size: 1.1rem;
`;

const SortLabel = styled.label`
  padding-right: 5px;
`;

const SortSelect = styled.select`
  border: 1px solid ${c.bgSlateBlue};
  font-size: 1.1rem;
`;
const SortOption = styled.option``;

const TasksDisplay = styled.div`
  ${flexbox()};
  width: 100%;
  height: 90%;
  overflow-y: auto;
`;
