import React, { useState } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";

import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";

const TaskCreation = ({ setPopupIsOpen }) => {
  const [colorway, setColorway] = useState("black");
  const [status, setStatus] = useState("todo");
  const [urgency, seturgency] = useState("low");

  const tasksArrays = useSelector(state => state.updateArraysReducer);

  const todoArray = tasksArrays.todoArray;
  const inProgressArray = tasksArrays.inProgressArray;
  const completedTasksArray = tasksArrays.completedTasksArray;
  let tasksCounter = tasksArrays.tasksCounter;

  const updatingArraysInDataBase = async () => {
    tasksCounter += 1;

    console.log(tasksCounter);

    const updatedArrays = {
      todoArray,
      inProgressArray,
      completedTasksArray,
      tasksCounter: tasksCounter + 1
    };

    const putMethod = {
      method: "PUT",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
        "auth-token": localStorage.getItem("token")
      },
      body: JSON.stringify(updatedArrays)
    };

    await fetch(
      `https://tracker-database.youngwebdevs.now.sh/dashboard/updatingarrays`,
      putMethod
    );

    console.log("PUT ENDED");
  };

  const createTask = e => {
    e.preventDefault();
    setPopupIsOpen(false);

    const title = e.target[0].value;
    const description = e.target[1].value;
    const date = e.target[2].value;

    const taskObject = {
      tasksCounter,
      title,
      description,
      date,
      colorway,
      status,
      urgency
    };

    switch (status) {
      case "todoArray":
        todoArray.push(taskObject);
        updatingArraysInDataBase();
        break;
      case "inProgressArray":
        inProgressArray.push(taskObject);
        updatingArraysInDataBase();
        break;
      case "completedTasksArray":
        completedTasksArray.push(taskObject);
        updatingArraysInDataBase();
        break;
      default:
        break;
    }
  };

  return (
    <Container>
      <Header>Add New Deadline</Header>
      <TaskForm onSubmit={createTask}>
        <TitleInput type="text" placeholder="Task Title..." />
        <DescrptionInput placeholder="Task Descrption..." />
        <DeadlineInput type="datetime-local" />
        <TaskColorPiker>
          Select Colorway:
          <ColorOptionBlack
            onClick={() => setColorway("black")}
            colorway={colorway}
          ></ColorOptionBlack>
          <ColorOptionBlue
            onClick={() => setColorway("blue")}
            colorway={colorway}
          ></ColorOptionBlue>
          <ColorOptionGreen
            onClick={() => setColorway("green")}
            colorway={colorway}
          ></ColorOptionGreen>
          <ColorOptionPink
            onClick={() => setColorway("pink")}
            colorway={colorway}
          ></ColorOptionPink>
          <ColorOptionRed
            onClick={() => setColorway("red")}
            colorway={colorway}
          ></ColorOptionRed>
        </TaskColorPiker>
        <TaskStatusSelect>
          <StatusOption onClick={() => setStatus("todoArray")}>
            ToDo
          </StatusOption>
          <StatusOption onClick={() => setStatus("inProgressArray")}>
            InProgress
          </StatusOption>
          <StatusOption onClick={() => setStatus("completedTasksArray")}>
            Done
          </StatusOption>
        </TaskStatusSelect>
        <TaskurgencySelect>
          <StatusOption onClick={() => seturgency("low")}>low</StatusOption>
          <StatusOption onClick={() => seturgency("regular")}>
            regular
          </StatusOption>
          <StatusOption onClick={() => seturgency("high")}>high</StatusOption>
        </TaskurgencySelect>
        <SubmitButton type="submit">Add</SubmitButton>
      </TaskForm>
    </Container>
  );
};

export default TaskCreation;

const Container = styled.div`
  width: 100%;
  height: 100%;
  background-color: ${c.shyBlue};
  border-radius: 15px;
  box-shadow: -6px -6px 20px 0 rgba(242, 248, 250, 0.9),
    6px 6px 16px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
`;

const Header = styled.h1`
  text-align: center;
  font-size: 2rem;
  padding-bottom: 10px;
`;

const TaskForm = styled.form`
  ${flexbox({ dir: "column", jc: "space-between" })}
  flex-wrap: wrap;
  height: 90%;
`;

const TitleInput = styled.input`
  width: 90%;
  height: 10%;
`;
const DescrptionInput = styled.textarea`
  width: 90%;
  height: 30%;
`;
const DeadlineInput = styled.input``;

const TaskColorPiker = styled.div`
  ${flexbox()}
  width: 100%;
  height: 50px;
`;

const ColorOptionStyle = `
  background-color: black;
  color: white;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-left: 5px;

  :hover {
    opacity: 1;
    cursor: pointer;
  }
`;

const ColorOptionBlack = styled.div`
  ${ColorOptionStyle};
  opacity: ${({ colorway }) => (colorway === "black" ? "1" : "0.5")};
  border: ${({ colorway }) => (colorway === "black" ? "3px solid white" : "")};
`;
const ColorOptionBlue = styled.div`
  ${ColorOptionStyle};
  opacity: ${({ colorway }) => (colorway === "blue" ? "1" : "0.5")};
  border: ${({ colorway }) => (colorway === "blue" ? "3px solid white" : "")};
  background-color: blue;
`;
const ColorOptionGreen = styled.div`
  ${ColorOptionStyle};
  opacity: ${({ colorway }) => (colorway === "green" ? "1" : "0.5")};
  border: ${({ colorway }) => (colorway === "green" ? "3px solid white" : "")};
  background-color: green;
`;
const ColorOptionPink = styled.div`
  ${ColorOptionStyle};
  opacity: ${({ colorway }) => (colorway === "pink" ? "1" : "0.5")};
  border: ${({ colorway }) => (colorway === "pink" ? "3px solid white" : "")};
  background-color: pink;
`;
const ColorOptionRed = styled.div`
  ${ColorOptionStyle};
  opacity: ${({ colorway }) => (colorway === "red" ? "1" : "0.5")};
  border: ${({ colorway }) => (colorway === "red" ? "3px solid white" : "")};
  background-color: red;
`;
const TaskStatusSelect = styled.div`
  ${flexbox()}
  width: 100%;
  height: 50px;
`;

const TaskurgencySelect = styled.div`
  ${flexbox()}
  width: 100%;
  height: 50px;
`;

const StatusOption = styled.div`
  width: 50px;
  height: 50px;
  border: 1px solid red;
`;
const SubmitButton = styled.button``;
