import React from "react";
import styled from "styled-components";

import { flexbox } from "../../../../style/mixins";
import * as c from "../../../../style/colors";

import { GiReturnArrow } from "react-icons/gi";
import { ReactComponent as ExclamationSVG } from "../../../../assets/SVG/alert.svg";
import { ReactComponent as FireSVG } from "../../../../assets/SVG/fire.svg";
import { ReactComponent as AlertSVG } from "../../../../assets/SVG/warning-sign.svg";

const FaceSide = ({
  cardIsFlipped,
  setCardIsFlipped,
  colorwayStyle,
  title,
  description,
  urgency,
  dayText,
  monthText,
  dayNumber,
  year,
  time
}) => {
  return (
    <FaceSideBox>
      <OptionsButton
        onClick={() => setCardIsFlipped(!cardIsFlipped)}
        colorwayStyle={colorwayStyle}
      >
        <StyledArrowIcon />
      </OptionsButton>
      <DetailsSection>
        <HeaderArea>
          <UrgencyIconsContainer>
            <UrgencyIconBox>
              {urgency === "regular" && <AlertICON />}
              {urgency === "high" && <FireSVG />}
            </UrgencyIconBox>
          </UrgencyIconsContainer>
          <Title>{title}</Title>
        </HeaderArea>
        <InfoArea colorwayStyle={colorwayStyle}>
          <Description>{description}</Description>
          {/* <Deadline>{`Deadline is at ${date}, ${time}`}</Deadline> */}
          <Deadline>
            {`  Deadline: ${dayText}
            ${dayNumber}
            ${monthText},
            ${year},
            ${time}`}
          </Deadline>
        </InfoArea>
      </DetailsSection>
    </FaceSideBox>
  );
};

export default FaceSide;

const FaceSideBox = styled.div`
  position: absolute;
  backface-visibility: hidden;
  height: 100%;
  width: 100%;
  perspective: 900px;
`;

const StyledArrowIcon = styled(GiReturnArrow)`
  transition: 2s infinite;

  :hover {
    filter: drop-shadow(0 0 5px gray);
  }
`;

const OptionsButton = styled.div`
  ${flexbox()};
  position: absolute;
  z-index: 1;
  right: 0;
  top: 0;
  height: 30%;
  width: 50px;
  font-size: 1.4rem;
  transform: rotateY(180deg) rotateX(180deg);
  border-radius: 10px;
  background: white;
  transition: 0.3s ease-in-out;

  :hover {
    cursor: pointer;
  }
`;

const DetailsSection = styled.div`
  ${flexbox({ dir: "column", jc: "space-around" })}
  height: 100%;
  width: 100%;
`;

const HeaderArea = styled.div`
  ${flexbox};
  flex-basis: 30%;
  height: 30%;
  width: 100%;
  border-radius: 10px;
`;

const UrgencyIconsContainer = styled.div`
  height: 100%;
  width: 3%;
  flex-basis: 3%;
  position: relative;
`;

const UrgencyIconBox = styled.div`
  position: absolute;
  height: 32px;
  width: 32px;
  left: -16px;
  top: -16px;
`;

const AlertICON = styled(AlertSVG)`
  fill: firebrick;
`;

const Title = styled.div`
  ${flexbox({ jc: "flex-start" })}
  height: 100%;
  width: 97%;
  flex-basis: 97%;
  font-size: 1.5rem;
  font-weight: bold;
  color: ${c.darkText};
  border-radius: 10px;

  overflow-x: auto;
`;

const InfoArea = styled.div`
  ${flexbox({ dir: "column" })};
  flex-basis: 70%;
  height: 70%;
  width: 100%;
  border-radius: 10px 0 10px 10px;
  background: ${({ colorwayStyle }) => colorwayStyle};
`;

const Description = styled.p`
  ${flexbox({ jc: "flex-start", ai: "flex-start" })}
  flex-basis: 80%;
  height: 80%;
  width: 100%;
  border-radius: 10px 0 0 0;
  padding: 10px;
  color: white;
  font-size: 0.9rem;
  line-height: 1.2rem;
  white-space: pre-wrap;

  overflow: auto;
`;

const Deadline = styled.div`
  ${flexbox({ jc: "flex-start", ai: "flex-end" })}
  flex-basis: 20%;
  height: 20%;
  width: 100%;
  background: ${({ colorwayStyle }) => colorwayStyle};
  border-radius: 0 0 10px 10px;
  font-weight: bold;
  padding: 0 0 8px 10px;
  color: ${c.shyRed};
  font-size: 0.9rem;
`;
