import React, { useState } from "react";
import styled from "styled-components";

import { flexbox } from "../../style/mixins";

const TaskCreation = () => {
  const [colorway, setColorway] = useState("blue");
  const [status, setStatus] = useState("todo");

  const createTask = e => {
    e.preventDefault();

    const title = e.target[0].value;
    const description = e.target[1].value;
    const date = e.target[2].value;

    console.log(e.target[0].value);
    console.log(e.target[1].value);
    console.log(e.target[2].value);

    const taskObject = {
      title,
      description,
      date,
      status,
      colorway
    };

    console.log({ taskObject });
  };

  return (
    <Wrapper>
      <Header>New Task</Header>
      <TaskForm id="form" onSubmit={createTask}>
        <TitleInput type="text" placeholder="Task Title..." />
        <DescrptionInput placeholder="Task Descrption..." />
        <DeadlineInput type="datetime-local" />
        <TaskColorPiker>
          <ColorOption1 onClick={() => setColorway("blue")}>red</ColorOption1>
          <ColorOption2 onClick={() => setColorway("red")}>blue</ColorOption2>
          <ColorOption3 onClick={() => setColorway("green")}>
            green
          </ColorOption3>
          <ColorOption4 onClick={() => setColorway("black")}>
            black
          </ColorOption4>
          <ColorOption5 onClick={() => setColorway("orange")}>
            orange
          </ColorOption5>
        </TaskColorPiker>
        <TaskStatusSelect>
          <StatusOption onClick={() => setStatus("todo")}>ToDo</StatusOption>
          <StatusOption onClick={() => setStatus("inProgress")}>
            InProgress
          </StatusOption>
          <StatusOption onClick={() => setStatus("done")}>Done</StatusOption>
        </TaskStatusSelect>
        <SubmitButton type="submit">Add</SubmitButton>
      </TaskForm>
    </Wrapper>
  );
};

export default TaskCreation;

const Wrapper = styled.div`
  border: 1px solid black;
  width: 100%;
  background-color: peachpuff;
`;
const Header = styled.h1`
  text-align: center;
  font-size: 2rem;
  padding-bottom: 10px;
`;
const TaskForm = styled.form`
  ${flexbox({ dir: "column", jc: "space-between" })}
  flex-wrap: wrap;
  height: 400px;
`;
const TitleInput = styled.input``;
const DescrptionInput = styled.textarea``;
const DeadlineInput = styled.input``;

const TaskColorPiker = styled.div`
  ${flexbox()}
  width: 100%;
  height: 50px;
`;

const ColorOption1 = styled.div`
  background-color: blue;
  color: white;
  width: 50px;
  height: 50px;
`;
const ColorOption2 = styled(ColorOption1)`
  background-color: red;
`;
const ColorOption3 = styled(ColorOption1)`
  background-color: green;
`;
const ColorOption4 = styled(ColorOption1)`
  background-color: black;
`;
const ColorOption5 = styled(ColorOption1)`
  background-color: orange;
`;
const TaskStatusSelect = styled.div`
  ${flexbox()}
  width: 100%;
  height: 50px;
`;

const StatusOption = styled.div`
  width: 50px;
  height: 50px;
  border: 1px solid red;
`;
const SubmitButton = styled.button``;
