import { SORTTASKS } from "../actions/sortTasksAction";

const sortTasksByReducer = (state = false, action) => {
  switch (action.type) {
    case SORTTASKS:
      return action.payload;
      break;
    default:
      return state;
  }
};

export default sortTasksByReducer;
