import React, { useState } from "react";
import styled from "styled-components";
import { flexbox } from "../../../style/mixins";

const TaskUrgencySelect = ({ urgency, setUrgency }) => {
  return (
    <TaskUrgencySelectBox>
      <UrgencyOption onClick={() => setUrgency("low")} urgency={urgency}>
        Low
      </UrgencyOption>
      <UrgencyOption onClick={() => setUrgency("regular")} urgency={urgency}>
        Regular
      </UrgencyOption>
      <UrgencyOption onClick={() => setUrgency("high")} urgency={urgency}>
        High
      </UrgencyOption>
    </TaskUrgencySelectBox>
  );
};

export default TaskUrgencySelect;

const TaskUrgencySelectBox = styled.div`
  ${flexbox()}
  width: 50%;
  height: 50px;
  border-radius: 5px;
  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);
`;

const UrgencyOption = styled.div`
  ${flexbox()}
  flex-basis: 33.333%;
  height: 50px;

  :hover {
    cursor: pointer;
    filter: brightness(2);
  }
`;
