import React, { useState, useEffect } from "react";
import styled from "styled-components";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import Task from "../Task/Task";
//-------- Redux --------//
import { markTodo } from "../../../state/actions/markTabAction";
import { useSelector, useDispatch } from "react-redux";
import { useInView } from "react-intersection-observer";

import TasksLister from "../TasksLister";

const Todo = ({ desktopView }) => {
  const [refreshCounter, setRefreshCounter] = useState(0);
  const [arrayToMap, setArrayToMap] = useState([]);
  const [ref, inView] = useInView({ threshold: 0.5 });

  const dispatch = useDispatch();
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArrays = useSelector(state => state.updateArraysReducer);
  const filteringArrays = useSelector(state => state.filteringArraysReducer);
  const toggleView = useSelector(state => state.toggleViewReducer);

  useEffect(() => {
    dispatchCurrentTab();
  }, [inView]);
  const dispatchCurrentTab = () => {
    if (inView) {
      dispatch(markTodo());
    }
  };

  return (
    <Wrapper id="todo" ref={ref} desktopView={desktopView}>
      <TasksLister
        arrayToMap={tasksArrays.todoArray}
        firstButtonText={"Forward to In Progress"}
        secondButtonText={"Forward to Done"}
      />
    </Wrapper>
  );
};

export default Todo;

const Wrapper = styled.div`
  vertical-align: top;
  ${flexbox()};
  scroll-snap-align: center;
  scroll-snap-stop: always;
  display: inline-block;
  width: 100%;
  height: 100%;

  background: ${c.gradientBlue};
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.15),
    inset -2px -2px 8px rgba(242, 248, 250, 0.2);

  @media (min-width: 767px) {
    width: ${({ desktopView }) => (desktopView ? "33.333%" : "100%")};
  }
`;

