import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";
import { useDispatch, useSelector } from "react-redux";

import { MdKeyboardArrowRight } from "react-icons/md";

import { filteringArraysAction } from "../../state/actions/filteredTasksAction";

import TasksLister from "./TasksLister";

const SearchScreen = ({ searchIsOpen, setSearchIsOpen }) => {
  const dispatch = useDispatch();

  const [searchInput, setSearchInput] = useState("");

  const loggedUser = useSelector(state => state.userReducer[0]);
  const currentTab = useSelector(state => state.markTabReducer);
  const tasksArray = useSelector(state => state.filteringArraysReducer);

  const getSearchInput = e => {
    e.preventDefault();
    if (e.target.value === "") {
      dispatch(filteringArraysAction([]));
    } else {
      setSearchInput(e.target.value);
      let searchInput = e.target.value;
      dispatch(
        filteringArraysAction([
          ...loggedUser.todoArray,
          ...loggedUser.inProgressArray,
          ...loggedUser.completedTasksArray
        ])
      );
      const filteringArrays = {
        todoArray: loggedUser.todoArray,
        inProgressArray: loggedUser.inProgressArray,
        completedTasksArray: loggedUser.completedTasksArray
      };
      const filteredTasks = arrrayToFilter =>
        arrrayToFilter.filter(task =>
          task.title.toUpperCase().includes(searchInput.toUpperCase())
        );

      let arrayAfterFiltering = [];

      switch (currentTab) {
        case "currentTabTodo":
          arrayAfterFiltering = filteredTasks(filteringArrays.todoArray);
          dispatch(
            filteringArraysAction([
              {
                todoArray: arrayAfterFiltering,
                inProgressArray: loggedUser.inProgressArray,
                completedTasksArray: loggedUser.completedTasksArray
              }
            ])
          );
          break;
        case "currentTabProgress":
          arrayAfterFiltering = filteredTasks(filteringArrays.inProgressArray);
          dispatch(
            filteringArraysAction([
              {
                todoArray: arrayAfterFiltering,
                inProgressArray: loggedUser.inProgressArray,
                completedTasksArray: loggedUser.completedTasksArray
              }
            ])
          );
          break;
        case "currentTabDone":
          arrayAfterFiltering = filteredTasks(
            filteringArrays.completedTasksArray
          );
          dispatch(
            filteringArraysAction([
              {
                todoArray: arrayAfterFiltering,
                inProgressArray: loggedUser.inProgressArray,
                completedTasksArray: loggedUser.completedTasksArray
              }
            ])
          );
          break;

        default:
          break;
      }
      //   dispatch(filteringArraysAction(arrayAfterFiltering));
    }
  };

  return (
    <SearchScreenBox searchIsOpen={searchIsOpen}>
      <SearchBar>
        <SearchHeadSction>
          <SearchHeader>Search</SearchHeader>
        </SearchHeadSction>
        <SearchForm>
          <SearchInput onChange={getSearchInput} id="searchInput" />
          <SearchButtonSubmit />
        </SearchForm>
        {/* <TasksDisplay>{<TasksLister />}</TasksDisplay> */}
      </SearchBar>
    </SearchScreenBox>
  );
};

export default SearchScreen;

const SearchScreenBox = styled.div`
  ${flexbox()};
  display: ${({ searchIsOpen }) => (searchIsOpen ? "block" : "none")};
  width: 100%;
  height: 100%;
  background: linear-gradient(to bottom, #bbd2c5, #536976);
  position: absolute;
  z-index: 7;
`;

const SearchHeadSction = styled.div`
  ${flexbox()};
  height: 10%;
`;

const SearchHeader = styled.h1`
  width: 100%;
  font-size: 3rem;
  text-align: center;
  color: white;
  text-shadow: 3px 3px 0 #000, -1px -1px 0 #000, 1px -1px 0 #000,
    -1px 1px 0 #000, 1px 1px 0 #000;
  font-family: "Titillium Web", sans-serif;
  letter-spacing: 3px;
`;
const CloseButton = styled.div``;

const SearchBar = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  z-index: 5;
`;

const SearchForm = styled.form`
  ${flexbox()}
  width: 100%;
  position: relative;
`;

const SearchInput = styled.input`
  border: 1px solid ${c.bgSlateBlue};
  width: 250px;
  font-size: 1.1rem;
  /* width: 130px; */
`;

const SearchButtonSubmit = styled(MdKeyboardArrowRight)`
  position: relative;
  right: 17px;
  /* top: 3px; */
  background-color: white;
  :hover {
    cursor: pointer;
  }
`;

const TasksDisplay = styled.div``;
