import React from "react";
import styled from "styled-components";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import { TiArrowLoop } from "react-icons/ti";

const OptionsButtonComponent = ({ cardIsFlipped, setCardIsFlipped }) => {
  return (
    <OptionsButtonBox
      onClick={() => setCardIsFlipped(!cardIsFlipped)}
      colorwayStyle={colorwayStyle}
    >
      <StyledArrowIcon />
    </OptionsButtonBox>
  );
};

export default OptionsButtonComponent;

const StyledArrowIcon = styled(TiArrowLoop)`
  transition: 2s infinite;

  :hover {
    filter: drop-shadow(0 0 5px gray);
  }
`;

const OptionsButtonBox = styled.div`
  ${flexbox()};
  position: absolute;
  z-index: 1;
  right: 0;
  top: 0;
  height: 25%;
  width: 50px;
  font-size: 1.4rem;
  /* transform: rotateY(180deg) rotateX(180deg); */
  border-radius: 10px;
  background: white;
  transition: 0.3s ease-in-out;

  :hover {
    cursor: pointer;
  }
`;
