import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import _ from "lodash";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";

import { getUserAction } from "../../../state/actions/getUserAction";
import { updateArraysAction } from "../../../state/actions/updateArraysAction";

const Task = ({
  id,
  title,
  description,
  deadline,
  colorway,
  urgency,
  status,
  firstButtonText,
  secondButtonText
}) => {
  const [cardIsFlipped, setCardIsFlipped] = useState(false);

  const dispatch = useDispatch();
  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArrays = useSelector(state => state.updateArraysReducer);

  const todoArray = tasksArrays.todoArray;
  const inProgressArray = tasksArrays.inProgressArray;
  const completedTasksArray = tasksArrays.completedTasksArray;
  const tasksCounter = tasksArrays.tasksCounter;

  //--SERVER FUNCTIONS--//

  const updatingArraysInDataBase = async () => {
    const updatedArrays = {
      todoArray,
      inProgressArray,
      completedTasksArray,
      tasksCounter
    };

    const putMethod = {
      method: "PUT",
      headers: {
        "Content-type": "application/json; charset=UTF-8",
        "auth-token": localStorage.getItem("token")
      },
      body: JSON.stringify(updatedArrays) // We send data in JSON format
    };

    await fetch(
      `https://tracker-database.youngwebdevs.now.sh/dashboard/updatingarrays`,
      putMethod
    );
  };

  //--------------//

  let urgencyShadow = ``;

  const selectUrgency = (() => {
    switch (urgency) {
      case "high":
        urgencyShadow = `-6px -6px 16px 0 red,
        6px 6px 16px 0 red`;
        break;
      case "regular":
        urgencyShadow = `-6px -6px 10px 0 blue,
        6px 6px 10px 0 blue`;
        break;
      case "low":
        urgencyShadow = `-3px -3px 6px 0 rgba(242, 248, 250, 1),
        3px 3px 6px 0 rgba(54, 73, 79, 0.25)`;
        break;

      default:
        urgencyShadow = `-3px -3px 6px 0 rgba(242, 248, 250, 1),
      3px 3px 6px 0 rgba(54, 73, 79, 0.25)`;
        break;
    }
  })();

  let colorwayStyle = ``;

  const selectColorway = (() => {
    switch (colorway) {
      case "black":
        colorwayStyle = c.gradientBlue;
        break;
      case "blue":
        colorwayStyle = c.taskBlue;
        break;
      case "green":
        colorwayStyle = c.taskGreen;
        break;
      case "pink":
        colorwayStyle = c.taskPink;
        break;
      case "red":
        colorwayStyle = c.taskRed;
        break;

      default:
        colorwayStyle = c.taskBlack;
        break;
    }
  })();

  const selectChange = (
    arrayToRemoveFrom,
    arrayToAddInto,
    destination,
    deleteTask
  ) => {
    const selectedTask = _.remove(arrayToRemoveFrom, item => {
      return item.id === id;
    });

    if (deleteTask === true) {
      setCardIsFlipped(false);
      dispatch(updateArraysAction(arrayToRemoveFrom));
      updatingArraysInDataBase();
      return;
    }

    const newStatusSelectedTask = {
      ...selectedTask[0],
      status: destination
    };
    arrayToAddInto.push(newStatusSelectedTask);

    // WARNING: because JS is flexable, this way will create duplicates rather than run over the current key:value pair.
    // const updatedArrays = {
    //   arrayToRemoveFrom,
    //   arrayToAddInto
    // };

    const updatedArrays = {
      arrayToRemoveFrom: arrayToRemoveFrom,
      arrayToAddInto: arrayToAddInto
    };

    setCardIsFlipped(false);
    dispatch(updateArraysAction(updatedArrays));
  };

  const handleFirstMove = () => {
    switch (status) {
      case "todo":
        selectChange(todoArray, inProgressArray, "inProgress");
        updatingArraysInDataBase();
        break;
      case "inProgress":
        selectChange(inProgressArray, completedTasksArray, "done");
        updatingArraysInDataBase();
        break;
      case "done":
        selectChange(completedTasksArray, inProgressArray, "inProgress");
        updatingArraysInDataBase();
        break;
      default:
        break;
    }
  };

  const handleSecondMove = () => {
    switch (status) {
      case "todo":
        selectChange(todoArray, completedTasksArray, "done");
        updatingArraysInDataBase();
        break;
      case "inProgress":
        selectChange(inProgressArray, todoArray, "todo");
        updatingArraysInDataBase();
        break;
      case "done":
        selectChange(completedTasksArray, todoArray, "todo");
        updatingArraysInDataBase();
        break;
      default:
        break;
    }
  };

  const handleDelete = () => {
    switch (status) {
      case "todo":
        selectChange(todoArray, null, null, true);
        break;
      case "inProgress":
        selectChange(inProgressArray, null, null, true);
        break;
      case "done":
        selectChange(completedTasksArray, null, null, true);
        break;
      default:
        break;
    }
  };

  return (
    <TaskContainer cardIsFlipped={cardIsFlipped} urgencyShadow={urgencyShadow}>
      <FaceSide>
        <OptionsButton
          onClick={() => setCardIsFlipped(!cardIsFlipped)}
          colorwayStyle={colorwayStyle}
        >
          flip
        </OptionsButton>
        <DetailsSection>
          <HeaderArea>
            <Title>{title}</Title>
          </HeaderArea>
          <InfoArea colorwayStyle={colorwayStyle}>
            <Description>{description}</Description>
            <Deadline>{deadline}</Deadline>
          </InfoArea>
        </DetailsSection>
      </FaceSide>
      <BackSide>
        <OptionsButtonBack
          onClick={() => setCardIsFlipped(!cardIsFlipped)}
          colorwayStyle={colorwayStyle}
        >
          flip
        </OptionsButtonBack>

        <MainActionArea>
          <FirstMoveButton
            onClick={() => {
              handleFirstMove(status);
            }}
          >
            {firstButtonText}
            <HrMarker />
          </FirstMoveButton>
        </MainActionArea>
        <OtherActionsArea colorwayStyle={colorwayStyle}>
          <SecondMoveButton
            onClick={() => {
              handleSecondMove(status);
            }}
          >
            {secondButtonText}
            <HrMarker />
          </SecondMoveButton>
          <DeleteTaskButton
            onClick={() => {
              handleDelete(status);
            }}
          >
            Delete Tracker
            <HrMarker />
          </DeleteTaskButton>
        </OtherActionsArea>
      </BackSide>
    </TaskContainer>
  );
};

export default Task;

const TaskContainer = styled.div`
  ${flexbox()}
  border-radius: 5px;
  height: 30%;
  max-height: 200px;
  width: 90%;
  max-width: 500px;
  margin: 20px auto;
  position: relative;
  background: white;
  border-radius: 10px;

  /* box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25); */

  box-shadow: ${({ urgencyShadow }) => urgencyShadow};

  transition: transform 0.8s;
  transform-style: preserve-3d;

  transform: ${({ cardIsFlipped }) =>
    cardIsFlipped
      ? "perspective(2000) rotateY(180deg)"
      : "perspective(2000) rotateY(0deg)"};
`;

const FaceSide = styled.div`
  position: absolute;
  backface-visibility: hidden;
  height: 100%;
  width: 100%;
  perspective: 900px;
`;

const OptionsButton = styled.div`
  position: absolute;
  z-index: 2;
  right: 0;
  top: 0;
  height: 30%;
  width: 50px;
  /* background: ${c.bgSlateBlue}; */
  background: ${({ colorwayStyle }) => colorwayStyle};

  border-radius: 10px 10px 0 10px;

  :hover {
    cursor: pointer;
  }

  border: 1px solid red;
  ${flexbox()};

  border-radius: 0 10px 0 0;
  background: white;
  transition: 0.3s ease-in-out;


    
  
`;

const DetailsSection = styled.div`
  ${flexbox({ dir: "column", jc: "space-around" })}
  height: 100%;
  width: 100%;
`;

const HeaderArea = styled.div`
  ${flexbox};
  flex-basis: 30%;
  width: 100%;
`;

const Title = styled.div`
  ${flexbox({ jc: "flex-start" })}
  height: 100%;
  width: 100%;
  font-size: 1.5rem;
  font-weight: bold;
  padding-left: 10px;

  color: ${c.darkText};
`;

const InfoArea = styled.div`
  ${flexbox({ dir: "column" })};
  flex-basis: 70%;
  width: 100%;
  border-radius: 10px 0 10px 10px;
  background: ${({ colorwayStyle }) => colorwayStyle};
`;

const Description = styled.div`
  ${flexbox({ jc: "flex-start", ai: "flex-start" })}
  flex-basis: 80%;
  height: 80%;
  width: 100%;
  border-radius: 10px 0 0 0;
  padding: 10px;
  color: white;
  font-size: 0.9rem;
`;

const Deadline = styled.div`
  ${flexbox({ jc: "flex-start", ai: "flex-end" })}
  flex-basis: 20%;
  height: 20%;
  width: 100%;
  background: ${({ colorwayStyle }) => colorwayStyle};
  border-radius: 0 0 10px 10px;
  font-weight: bold;
  padding: 0 0 8px 10px;
  color: ${c.shyRed};
  font-size: 1rem;
`;

const BackSide = styled.div`
  position: absolute;
  backface-visibility: hidden;

  transform: rotateY(180deg);
  perspective: 900px;

  height: 100%;
  width: 100%;
`;

const OptionsButtonBack = styled(OptionsButton)`
  height: 33.333%;
`;

const HrMarker = styled.hr`
  width: 70%;
  height: 5px;
  border-radius: 20px;
  border: none;
  transition: 0.3s ease-out;
  position: absolute;
  bottom: 0;
`;

const MainActionArea = styled.div`
  ${flexbox()};
  flex-basis: 35%;
  height: 35%;
  width: 100%;

  border: 1px solid red;
`;

const FirstMoveButton = styled.div`
  ${flexbox()};
  position: relative;
  height: 100%;
  width: 100%;
  border-radius: 10px 10px 0 0;
  font-weight: bold;
  transition: 0.2s ease-out;
  :hover {
    cursor: pointer;
  }

  :hover ${HrMarker} {
    background: #4da6ff;
    box-shadow: inset 2px 2px 4px #9bcdff;
  }
`;

const OtherActionsArea = styled.div`
  ${flexbox({ dir: "column" })};
  flex-basis: 65%;
  height: 65%;
  width: 100%;
  background: ${({ colorwayStyle }) => colorwayStyle};
`;

const SecondMoveButton = styled(FirstMoveButton)`
  flex-basis: 50%;
  width: 100%;
  border-radius: 10px 0 0 0;
  color: white;
