import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";
import { useDispatch, useSelector } from "react-redux";

import { MdKeyboardArrowRight } from "react-icons/md";
import { FiSearch } from "react-icons/fi";

import { filteringArraysAction } from "../../state/actions/filteredTasksAction";

const Search = ({ searchIsOpen, setSearchIsOpen }) => {
  const dispatch = useDispatch();

  const [searchInput, setSearchInput] = useState("");

  const loggedUser = useSelector(state => state.userReducer[0]);
  const tasksArray = useSelector(state => state.filteringArraysReducer);

  const getSearchInput = e => {
    e.preventDefault();
    if (e.target.value === "") {
      dispatch(filteringArraysAction([]));
    } else {
      setSearchInput(e.target.value);
      let searchInput = e.target.value;
      filteringArraysAction({
        todoArray: loggedUser.todoArray,
        inProgressArray: loggedUser.inProgressArray,
        completedTasksArray: loggedUser.completedTasksArray
      });
      const filteringArrays = {
        todoArray: loggedUser.todoArray,
        inProgressArray: loggedUser.inProgressArray,
        completedTasksArray: loggedUser.completedTasksArray
      };
      const filteredTasks = tasksArray.filter(task =>
        task.title.toUpperCase().includes(searchInput.toUpperCase())
      );
      dispatch(filteringArraysAction([...filteredTasks]));
    }
  };

  return (
    <SearchBox>
      <SearchButton
        id="searchButton"
        onClick={() => setSearchIsOpen(!searchIsOpen)}
      >
        <FiSearch />
      </SearchButton>
      <SearchBar
        searchIsOpen={searchIsOpen}
        onClick={() => setSearchIsOpen(true)}
      >
        <SearchForm>
          <SearchInput onChange={getSearchInput} id="searchInput" />
          <SearchButtonSubmit />
        </SearchForm>
      </SearchBar>
    </SearchBox>
  );
};

export default Search;

const SearchBox = styled.div`
  ${flexbox()};
  flex-basis: 10%;
`;

const SearchButton = styled.div`
  ${flexbox()}
  text-align: center;
  font-size: 1.4rem;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  box-shadow: -2px -2px 6px 0 rgba(242, 248, 250, 0.9),
    2px 2px 6px 0 rgba(54, 73, 79, 0.5);
  border: 1px solid rgba(242, 248, 250, 0.6);
  :hover {
    cursor: pointer;
    filter: brightness(1.3);
  }
`;

const searchInputAnimation = keyframes`
0%{
  width: 0%;
}
100%{
  width: 150px;
}
`;

const SearchBar = styled.div`
  ${flexbox({ dir: "column" })}
  display: ${({ searchIsOpen }) => (searchIsOpen ? "block" : "none")};
  position: absolute;
  top: 55px;
  right: 7px;
  z-index: 5;
  border: 1px solid ${c.bgSlateBlue};
  border-radius: 2%;
  animation: ${searchInputAnimation} 0.2s forwards;
`;

const SearchForm = styled.form``;

const SearchInput = styled.input`
  animation: ${searchInputAnimation} 0.2s forwards;
  /* width: 130px; */
`;

const SearchButtonSubmit = styled(MdKeyboardArrowRight)`
  position: absolute;
  right: 1px;
  top: 3px;
  background-color: white;
`;
