import React from "react";
import styled from "styled-components";

import { flexbox } from "../../style/mixins";

const TaskCreation = () => {
  return (
    <Wrapper>
      <TaskForm id="taskCreate">
        <TitleInput type="text" />
        <DescrptionInput type="text" />
        <DeadlineInput type="datetime-local" />
        <TaskColorPiker>
          <ColorOption1 type="radio" name="colorOption" value="red" />
          <ColorLabel1>red</ColorLabel1>
          <ColorOption2 type="radio" name="colorOption" value="blue" />
          <ColorLabel2 for="colorOption2">blue</ColorLabel2>
          <ColorOption3 type="radio" name="colorOption" value="green" />
          <ColorLabel3 for="colorOption3">green</ColorLabel3>
          <ColorOption4 type="radio" name="colorOption" value="black" />
          <ColorLabel4 for="colorOption4">black</ColorLabel4>
          <ColorOption5 type="radio" name="colorOption" value="orange" />
          <ColorLabel5 for="colorOption5">orange</ColorLabel5>
        </TaskColorPiker>
        <SubmitButton type="submit">Add</SubmitButton>
      </TaskForm>
      <TaskTypeSelect name="type" form="taskCreate">
        <TypeOption value="ToDo">ToDo</TypeOption>
        <TypeOption value="InProgress">InProgress</TypeOption>
        <TypeOption value="Done">Done</TypeOption>
      </TaskTypeSelect>
    </Wrapper>
  );
};

export default TaskCreation;

const Wrapper = styled.div`
  border: 1px solid black;

  width: 100%;
  z-index: 1;
`;
const TaskForm = styled.div`
  height: 100%;
  ${flexbox()}
`;
const TitleInput = styled.input``;
const DescrptionInput = styled.input``;
const DeadlineInput = styled.input``;
const TaskColorPiker = styled.div``;
const ColorLabel1 = styled.label``;
const ColorLabel2 = styled(ColorLabel1)``;
const ColorLabel3 = styled(ColorLabel1)``;
const ColorLabel4 = styled(ColorLabel1)``;
const ColorLabel5 = styled(ColorLabel1)``;
const ColorOption1 = styled.input``;
const ColorOption2 = styled(ColorOption1)``;
const ColorOption3 = styled(ColorOption1)``;
const ColorOption4 = styled(ColorOption1)``;
const ColorOption5 = styled(ColorOption1)``;
const SubmitButton = styled.button``;
const TaskTypeSelect = styled.select``;
const TypeOption = styled.option``;
