import React from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import { Redirect, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { flexbox } from "../../style/mixins";

//-------Components-------//
import Todo from "./Todo/Todo";
import InProgress from "./InProgress/InProgress";
import Done from "./Done/Done";

//-------Actions--------//
import { importUserAction } from "../../state/actions/importUserAction";
import { isLoggedAction } from "../../state/actions/isLoggedAction";

const Dashbord = () => {
  const dispatch = useDispatch();
  const isLogged = useSelector(state => state.isLoggedReducer);
  const loggedUser = useSelector(state => state.userReducer);
  const signOut = () => {
    dispatch(importUserAction({}));
    dispatch(isLoggedAction({}));
  };
  return (
    <Wrapper>
      {/* {isLogged === false && <Redirect push to="/" />} */}
      <AppContainer>
        <HeaderContainer>
          <LogOutButton onClick={signOut}>Log Out</LogOutButton>
          <Header>Tracker</Header>
          <Logo>{loggedUser.avatar}</Logo>
        </HeaderContainer>

        <TabsContainer>
          <TodoTab>To Do</TodoTab>
          <InProgressTab>In Progress</InProgressTab>
          <DoneTab>Done</DoneTab>
        </TabsContainer>
        <TrackersContainer>
          <Todo />
          <InProgress />
          <Done />
          <AddTracker />
        </TrackersContainer>
      </AppContainer>
    </Wrapper>
  );
};

export default Dashbord;

const Wrapper = styled.div`
  ${flexbox()};
  height: 100%;
  width: 100%;
`;

const AppContainer = styled.div`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 100%;
  width: 100%;
  height: 100vh;
  max-width: 500px;
  border: 1px solid red;
`;

const HeaderContainer = styled.div`
  ${flexbox({ jc: "space-around" })};
  height: 50px;
  width: 100%;
  border: 1px solid red;
`;

const LogOutButton = styled.button`
  flex-basis: 10%;
`;

const Header = styled.h1`
  flex-basis: 70%;
  font-size: 2rem;
  text-align: center;
`;

const Logo = styled.h1`
  flex-basis: 10%;
`;

const TabsContainer = styled.div`
  ${flexbox({ jc: "space-evenly" })};
  width: 100%;
  height: 75px;
  border: 1px solid blue;
`;

const TodoTab = styled.div`
  ${flexbox()};
  flex-basis: 25%;
  height: 100%;
  border: 2px solid green;
  border-radius: 15px 15px 0 0;
`;

const InProgressTab = styled(TodoTab)``;
const DoneTab = styled(TodoTab)``;

const TrackersContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  background: lightyellow;
`;

const AddTracker = styled.div`
  position: absolute;
  right: 10px;
  bottom: 10px;
  border: 1px solid blue;
  border-radius: 50%;
  height: 50px;
  width: 50px;
`;
