import React from "react";
import styled from "styled-components";
import { flexbox } from "../../../style/mixins";

const TaskStatusSelect = ({ status, setStatus }) => {
  return (
    <TaskStatusSelectBox>
      <TodoOption onClick={() => setStatus("todo")} status={status}>
        ToDo
      </TodoOption>
      <InProgressOption onClick={() => setStatus("inProgress")} status={status}>
        In Progress
      </InProgressOption>
      <DoneOption onClick={() => setStatus("done")} status={status}>
        Done
      </DoneOption>
    </TaskStatusSelectBox>
  );
};

export default TaskStatusSelect;

const TaskStatusSelectBox = styled.div`
  ${flexbox()}
  width: 50%;
  height: 50px;
  border-radius: 5px;
  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);

  font-size: 0.8rem;
`;

const StatusOptionStyle = `
  ${flexbox()}
  flex-basis: 33.333%;
  height: 50px;

  :hover {
    cursor: pointer;
    font-weight: bold;
  }
`;

const TodoOption = styled.div`
  ${StatusOptionStyle}

  box-shadow: ${({ status }) =>
    status === "todo"
      ? "inset 2px 2px 4px 1px rgba(54, 73, 79, 0.50), inset -2px -2px 4px 1px rgba(242, 248, 250, 0.50)"
      : "2px 2px 4px 1px rgba(54, 73, 79, 0.50), -2px -2px 4px 1px rgba(242, 248, 250, 0.50)"};

  border-radius: 5px 0 0 5px;
`;

const InProgressOption = styled.div`
  ${StatusOptionStyle}

  box-shadow: ${({ status }) =>
    status === "inProgress"
      ? "inset 2px 2px 4px 1px rgba(54, 73, 79, 0.50), inset -2px -2px 4px 1px rgba(242, 248, 250, 0.50)"
      : ""};

      position: relative;

  ::before{
    content: "";
    position: absolute;
    left: 0;
    height: 50%;
    width: 2px;
    background: black;
    opacity: 0.2;
  }
  ::after{
    content: "";
    position: absolute;
    right: 0;
    height: 50%;
    width: 1px;
    background: black;
    opacity: 0.2;
  }
`;
const DoneOption = styled.div`
  ${StatusOptionStyle}

  box-shadow: ${({ status }) =>
    status === "done"
      ? "inset 2px 2px 4px 1px rgba(54, 73, 79, 0.50), inset -2px -2px 4px 1px rgba(242, 248, 250, 0.50)"
      : ""};

  border-radius: 0 5px 5px 0;
`;
