import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import _ from "lodash";

import { flexbox } from "../../../style/mixins";
import * as c from "../../../style/colors";
import { GiReturnArrow } from "react-icons/gi";

import { getUserAction } from "../../../state/actions/getUserAction";
import { updateArraysAction } from "../../../state/actions/updateArraysAction";

//---IMPORT COMPONENTS---//
import FaceSide from "./FaceSide/FaceSide";
import BackSide from "./BackSide/BackSide";

const Task = ({
  id,
  title,
  description,
  deadline,
  colorway,
  urgency,
  status,
  firstButtonText,
  secondButtonText
}) => {
  const [cardIsFlipped, setCardIsFlipped] = useState(false);

  const dispatch = useDispatch();
  const loggedUser = useSelector(state => state.userReducer[0]);

  let urgencyShadow = ``;

  const selectUrgency = (() => {
    switch (urgency) {
      case "high":
        urgencyShadow = `-2px -2px 10px 0 rgba(255, 84, 90, 0.7)
        `;
        break;
      case "regular":
        urgencyShadow = `-2px -2px 6px 0 rgba(84, 127, 255, 0.7)
        `;
        break;
      case "low":
        urgencyShadow = `-2px -2px 6px 0 rgba(242, 248, 250, 1),
        2px 2px 6px 0 rgba(54, 73, 79, 0.5)`;
        break;

      default:
        urgencyShadow = `-3px -3px 6px 0 rgba(242, 248, 250, 1),
      3px 3px 6px 0 rgba(54, 73, 79, 0.5)`;
        break;
    }
  })();

  let colorwayStyle = ``;

  const selectColorway = (() => {
    switch (colorway) {
      case "black":
        colorwayStyle = c.taskBlack;
        break;
      case "blue":
        colorwayStyle = c.taskBlue;
        break;
      case "turquoise":
        colorwayStyle = c.taskTurquoise;
        break;
      case "green":
        colorwayStyle = c.taskGreen;
        break;
      case "pink":
        colorwayStyle = c.taskPink;
        break;

      default:
        colorwayStyle = c.taskBlack;
        break;
    }
  })();

  return (
    <TaskContainer cardIsFlipped={cardIsFlipped} urgencyShadow={urgencyShadow}>
      <FaceSide
        cardIsFlipped={cardIsFlipped}
        setCardIsFlipped={setCardIsFlipped}
        colorwayStyle={colorwayStyle}
        title={title}
        description={description}
        deadline={deadline}
      />
      <BackSide
        cardIsFlipped={cardIsFlipped}
        setCardIsFlipped={setCardIsFlipped}
        colorwayStyle={colorwayStyle}
        id={id}
        status={status}
        firstButtonText={firstButtonText}
        secondButtonText={secondButtonText}
      />
    </TaskContainer>
  );
};

export default Task;

const TaskContainer = styled.div`
  ${flexbox()}
  border-radius: 10px;
  height: 30%;
  max-height: 200px;
  width: 90%;
  max-width: 500px;
  margin: 20px auto;
  position: relative;
  background: white;
  border-radius: 10px;
  box-shadow: ${({ urgencyShadow }) => urgencyShadow};

  transition: transform 0.8s;
  transform-style: preserve-3d;

  transform: ${({ cardIsFlipped }) =>
    cardIsFlipped
      ? "perspective(2000) rotateY(180deg)"
      : "perspective(2000) rotateY(0deg)"};
`;
