import React from "react";
import styled from "styled-components";

import { flexbox } from "../../style/mixns";

const SignUp = () => {
  return (
    <Wrapper>
      <Header>
        Sign Up <span>:)</span>
      </Header>
      <SignUpContainer>
        <AvatarDisplay />
        <UserName />
        <UserEmail />
        <UserPassword />
        <ConfirmPassword />
      </SignUpContainer>
    </Wrapper>
  );
};

export default SignUp;

const Wrapper = styled.div`
  height: 100%;
  width: 100%;

  ${flexbox({ dir: "column", jc: "space-evenly" })};
`;

const Header = styled.div`
  flex-basis: 10%;
  font-size: 4rem;
  font-family: "Titillium Web", sans-serif;

  span {
    color: red;
    font-size: 5rem;
    font-family: "Montserrat", sans-serif;
  }
`;

const SignUpContainer = styled.form`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 65%;
  width: 80%;
  border-radius: 15px;
  box-shadow: -6px -6px 20px 0 rgba(242, 248, 250, 0.9),
    6px 6px 16px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
  padding: 20px;

  max-height: 875px;
  max-width: 600px;
`;

const AvatarDisplay = styled.div`
  height: 50px;
  width: 50px;
  border: 1px solid red;
`;

const UserName = styled.input`
  width: 100%;
  height: 15%;
  border-radius: 5px;
  border: none;
  background: rgba(242, 248, 250, 0.55);
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.25),
    inset -2px -2px 8px rgba(242, 248, 250, 0.9);
  padding-left: 10px;
  font-size: 0.8rem;
  color: #3c5157;

  opacity: 0.7;
  transition: 0.3s ease-out;

  outline: none;

  &:hover,
  &:focus {
    opacity: 1;
  }
`;
const UserEmail = styled(UserName)``;
const UserPassword = styled(UserName)``;
const ConfirmPassword = styled(UserName)``;
