import React from "react";
import styled from "styled-components";
import { flexbox } from "../../../style/mixins";

const Task = ({ title, description, deadline, colorWay }) => {
  return (
    <TaskContainer>
      <OptionsButton>
        <div>flip</div>
      </OptionsButton>
      <DetailsSection>
        <Title>{title}</Title>
        <Description>{description}</Description>
        <Deadline>{deadline}</Deadline>
      </DetailsSection>
    </TaskContainer>
  );
};

export default Task;

const TaskContainer = styled.div`
  ${flexbox()}
  border-radius: 5px;
  height: 30%;
  width: 90%;
  margin: 5px;
  position: relative;
  background: white;
  border-radius: 10px;
`;

const OptionsButton = styled.div`
  position: absolute;
  right: 0;
  top: 0;
  height: 35px;
  width: 35px;
  background: lightsteelblue;
  border-radius: 10px 10px 0 10px;

  div {
    ${flexbox()};

    height: 100%;
    width: 100%;
    border-radius: 10px;
    background: lightcoral;
  }
`;

const DetailsSection = styled.div`
  ${flexbox({ dir: "column", jc: "space-around" })}
  height: 100%;
  width: 100%;
`;

const Title = styled.div`
  flex-basis: 15%;
  width: 100%;
  font-size: 1.3rem;
  font-weight: bold;
  padding: 5px;
`;
const Description = styled.div`
  flex-basis: 70%;
  height: 80%;
  width: 100%;
  border-radius: 10px 0 0 0;
  background: lightsteelblue;
  padding: 5px;
`;
const Deadline = styled.div`
  flex-basis: 15%;
  width: 100%;
  padding: 5px;
  background: lightsteelblue;
  border-radius: 0 0 10px 10px;
`;
