import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import { Redirect, Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";

//-------Components-------//
import Todo from "./Todo/Todo";
import InProgress from "./InProgress/InProgress";
import Done from "./Done/Done";
import TaskCreation from "./TaskCreation/TaskCreation";

//-------Actions--------//
import { getUserAction } from "../../state/actions/getUserAction";
import { isLoggedAction } from "../../state/actions/isLoggedAction";
import { updateArraysAction } from "../../state/actions/updateArraysAction";

const Dashbord = () => {
  const [popupIsOpen, setPopupIsOpen] = useState(false);
  const [flag, setFlag] = useState(true);
  const [dropDownIsOpen, setDropDownIsOpen] = useState(false);

  const currentTab = useSelector(state => state.markTabReducer);
  const dispatch = useDispatch();
  const isLogged = useSelector(state => state.isLoggedReducer);
  const loggedUser = useSelector(state => state.userReducer[0]);

  const signOut = () => {
    localStorage.removeItem("token");
    dispatch(getUserAction({}));
    dispatch(isLoggedAction({}));
  };

  const oneTime = (() => {
    if (flag) {
      dispatch(
        updateArraysAction({
          tasksCounter: loggedUser.tasksCounter,
          todoArray: loggedUser.todoArray,
          inProgressArray: loggedUser.inProgressArray,
          completedTasksArray: loggedUser.completedTasksArray
        })
      );
      setFlag(false);
    }
  })();

  return (
    <Wrapper>
      {isLogged === false && <Redirect push to="/" />}
      <AppContainer>
        <TaskCreationWrapper
          id="close"
          popupIsOpen={popupIsOpen}
          onClick={e => e.target.id === "close" && setPopupIsOpen(false)}
        >
          <TaskCreationContainer>
            <TaskCreation setPopupIsOpen={setPopupIsOpen} />
          </TaskCreationContainer>
        </TaskCreationWrapper>

        <HeaderContainer>
          <Logo onClick={() => setDropDownIsOpen(!dropDownIsOpen)}>
            <LogoImg avatarImg={loggedUser.avatar} />
            <DropDownMenu dropDownIsOpen={dropDownIsOpen}>
              <CreditsButton>Credits</CreditsButton>
              <LogOutButton onClick={signOut}>Log Out</LogOutButton>
            </DropDownMenu>
          </Logo>
          <Header>
            Dead<span>l</span>ine
          </Header>
          <SearchButton />
        </HeaderContainer>

        <TabsContainer>
          <TodoTab href="#todo" currentTab={currentTab}>
            To Do
          </TodoTab>
          <InProgressTab href="#progress" currentTab={currentTab}>
            In Progress
          </InProgressTab>
          <DoneTab href="#done" currentTab={currentTab}>
            Done
          </DoneTab>
        </TabsContainer>

        <TrackersContainer>
          <PageCarousel>
            <Todo />
            <InProgress />
            <Done />
          </PageCarousel>
          <AddTrackerButton onClick={() => setPopupIsOpen(true)}>
            +<span> l</span>
          </AddTrackerButton>
        </TrackersContainer>
      </AppContainer>
    </Wrapper>
  );
};

export default Dashbord;

const Wrapper = styled.div`
  ${flexbox()};
  height: 100%;
  width: 100%;
`;

const AppContainer = styled.div`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 100%;
  width: 100%;
  height: 100vh;
  max-width: 500px;
  position: relative;
`;

const TaskCreationWrapper = styled.div`
  ${flexbox()}
  width: 100%;
  height: 100%;
  position: absolute;
  z-index: 4;
  background: rgba(0, 0, 0, 0.8);
  background: ${c.shyBlue};
  transition: 0.2s ease-out;
  transform: ${({ popupIsOpen }) => (popupIsOpen ? "scale(1)" : "scale(0)")};
`;

const TaskCreationContainer = styled.div`
  width: 95%;
  height: 70%;
  position: absolute;
  z-index: 5;

  @media (max-width: 450px) {
    height: 80%;
  }
`;

const HeaderContainer = styled.div`
  ${flexbox({ jc: "space-around" })};
  height: 70px;
  width: 100%;
`;

const Header = styled.h1`
  color: ${c.darkBlueText};
  flex-basis: 65%;
  font-size: 2rem;
  text-align: center;
  font-family: "Titillium Web", sans-serif;
  span {
    color: red;
  }
`;

const SearchButton = styled.div``;

const Logo = styled.div`
  text-align: center;
  flex-basis: 10%;
  position: relative;
`;

const LogoImg = styled.div`
  ${flexbox()};
  box-shadow: -3px -3px 7px 0 rgba(242, 248, 250, 0.9),
    2px 2px 6px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
  opacity: 0.8;
  height: 45px;
  width: 45px;
  margin-left: 2px;
  border-radius: 50%;
  background: ${({ avatarImg }) => `url(${avatarImg})`};
  background-position: center;
  background-size: cover;
`;

const DropDownMenu = styled.div`
  ${flexbox({ dir: "column" })}
  position: absolute;
  margin-top: 10px;

  z-index: 3;
  display: ${({ dropDownIsOpen }) => (dropDownIsOpen ? "block" : "none")};
  height: 100px;
  width: 200px;
  border: 1px solid red;
  border-radius: 5px;
`;
const CreditsButton = styled.div`
  ${flexbox()}
  flex-basis: 50%;
  height: 50%;
  width: 100%;
  border-radius: 5px 5px 0 0;
  /* box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25); */
  border: none;
  background: ${c.shyBlue};

  :hover {
    cursor: pointer;
  }
`;

const LogOutButton = styled(CreditsButton)`
  border-radius: 0 0 5px 5px;
`;

const TabsContainer = styled.div`
  ${flexbox({ jc: "space-evenly" })};
  width: 100%;
  height: 55px;
  box-shadow: 0px 5px 5px 0px rgba(0, 0, 0, 0.15);
  position: relative;
  z-index: 1;
  font-weight: bold;
  font-size: 1.1rem;
`;

const TodoTab = styled.a`
  ${flexbox()};
  flex-basis: 33%;
  height: 100%;
  border-radius: 15px 15px 0 0;
  color: black;
  transition: 0.2s ease-out;
  text-decoration: none;
  opacity: ${({ currentTab }) =>
    currentTab === "currentTabTodo" ? "1" : "0.4"};
`;

const InProgressTab = styled(TodoTab)`
  opacity: ${({ currentTab }) =>
    currentTab === "currentTabProgress" ? "1" : "0.4"};
`;
const DoneTab = styled(TodoTab)`
  opacity: ${({ currentTab }) =>
    currentTab === "currentTabDone" ? "1" : "0.4"};
`;

const TrackersContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
`;

const PageCarousel = styled.div`
  height: 100%;
  width: 100%;
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;
  scroll-snap-type: x mandatory;
  scroll-behavior: smooth;
`;

const AddTrackerButton = styled.div`
  ${flexbox()};
  font-size: 1rem;
  position: absolute;
  right: 20px;
  bottom: 20px;
  border-radius: 50%;
  height: 60px;
  width: 60px;
  font-family: "Titillium Web", sans-serif;

  border: 1px solid rgba(242, 248, 250, 0.2);
  box-shadow: -2px -2px 4px 0 rgba(242, 248, 250, 0.5),
    2px 2px 4px 0 rgba(54, 73, 79, 0.25);
  background: ${c.lightBlue};

  transition: 0.3s ease-out;
  opacity: 0.8;
  span {
    color: red;
    font-size: 2rem;
  }

  :hover {
    opacity: 1;
    cursor: pointer;
  }
`;
