import React, { useState } from "react";
import styled from "styled-components";

import { flexbox } from "../../style/mixns";

const SignUp = () => {
  const [avatar, setAvatar] = useState("?");
  return (
    <Wrapper>
      <Header>
        Sign Up <span>:)</span>
      </Header>
      <SignUpContainer>
        <AvatarDisplay avatar={avatar} />
        <UserName placeholder="Choose User Name..." />
        <UserEmail placeholder="Choose Email..." />
        <UserPassword placeholder="Choose Password..." />
        <ConfirmPassword placeholder="Confirm Password..." />
        <SignUpButton>Submit</SignUpButton>
      </SignUpContainer>
    </Wrapper>
  );
};

export default SignUp;

const Wrapper = styled.div`
  height: 100%;
  width: 100%;

  ${flexbox({ dir: "column", jc: "space-around" })};
`;

const Header = styled.div`
  flex-basis: 10%;
  font-size: 4rem;
  font-family: "Titillium Web", sans-serif;

  span {
    color: red;
    font-size: 5rem;
    font-family: "Montserrat", sans-serif;
  }
`;

const SignUpContainer = styled.form`
  ${flexbox({ dir: "column", jc: "flex-start" })};
  flex-basis: 65%;
  width: 80%;
  border-radius: 15px;
  box-shadow: -6px -6px 20px 0 rgba(242, 248, 250, 0.9),
    6px 6px 16px 0 rgba(54, 73, 79, 0.25);
  border: 1px solid rgba(242, 248, 250, 0.2);
  padding: 20px;

  max-height: 875px;
  max-width: 600px;
`;

const AvatarDisplay = styled.div`
  height: 50px;
  width: 50px;
  border: 1px solid red;
  border-radius: 50%;

  ::after {
    content: ${avatar};
    height: 100%;
    width: 100%;
    ${flexbox()}
    text-align: center;
    font-size: 2rem;
  }
`;

const UserName = styled.input`
  margin: 10px;
  width: 100%;
  height: 10%;
  border-radius: 5px;
  border: none;
  background: rgba(242, 248, 250, 0.55);
  box-shadow: inset 2px 2px 6px rgba(54, 73, 79, 0.25),
    inset -2px -2px 8px rgba(242, 248, 250, 0.9);
  padding-left: 10px;
  font-size: 0.8rem;
  color: #3c5157;

  opacity: 0.7;
  transition: 0.3s ease-out;

  outline: none;

  &:hover,
  &:focus {
    opacity: 1;
  }
`;
const UserEmail = styled(UserName)``;
const UserPassword = styled(UserName)``;
const ConfirmPassword = styled(UserName)``;
const SignUpButton = styled.button`
  width: 65%;
  height: 10%;
  border-radius: 5px;
  box-shadow: -3px -3px 6px 0 rgba(242, 248, 250, 1),
    3px 3px 6px 0 rgba(54, 73, 79, 0.25);
  border: none;
  background: #dfe8eb;
  font-size: 0.9rem;
  margin-bottom: 5px;
  color: #3c5157;
  text-shadow: 0 0 1px white;
  opacity: 0.5;
  transition: 0.3s ease-out;

  outline: none;

  &:hover,
  &:focus {
    opacity: 0.9;
    cursor: pointer;
    background: #e8faff;
  }
`;
