import React from "react";
import styled from "styled-components";
import { flexbox } from "../../style/mixins";
import * as c from "../../style/colors";
import { useSelector } from "react-redux";

import { TiExportOutline } from "react-icons/ti";

import { CSVLink, CSVDownload } from "react-csv";

const ExcelButton = () => {

  const tasksArrays = useSelector(state => state.updateArraysReducer);

  const csvData = [
    ["firstname", "lastname", "email"],
    ["Ahmed", "Tomi", "ah@smthing.co.com"],
    ["Raed", "Labes", "rl@smthing.co.com"],
    ["Yezzi", "Min l3b", "ymin@cocococo.com"]
  ];

  return (
    <ExcelButtonBox data={csvData}>
      Export
      <TiExportOutline />
    </ExcelButtonBox>
  );
};

export default ExcelButton;

const ExcelButtonBox = styled(CSVLink)`
  ${flexbox()}
  flex-basis: 25%;
  height: 25%;
  width: 100%;
  background: ${c.bgSlateBlue};
  font-weight: bold;
  font-size: 1.1rem;
  background: white;
  text-decoration: none;
  color: ${c.darkBlueText};
  transition: 0.2s ease-out;

  :hover {
    background: ${c.bgfadeBlue};
    cursor: pointer;
  }
`;
