import { TOGGLEVIEW } from "../actions/toggleViewAction";

const toggleViewReducer = (state = true, action) => {
  switch (action.type) {
    case TOGGLEVIEW:
      return action.payload;
      break;
    default:
      return state;
  }
};

export default toggleViewReducer;
