export const selectChange = (
  arrayToRemoveFrom,
  arrayToAddInto,
  destination,
  deleteTask
) => {
  const selectedTask = _.remove(arrayToRemoveFrom, n => {
    return n.id === id;
  });
  console.log("selectedtask: ", selectedTask[0]);

  if (deleteTask === true) {
    setCardIsFlipped(false);
    setRefreshCounter(refreshCounter + 1);
    dispatch(updateArraysAction(arrayToRemoveFrom));
    return;
  }

  const newStatusSelectedTask = {
    ...selectedTask[0],
    status: destination
  };
  arrayToAddInto.push(newStatusSelectedTask);

  const updatedArrays = {
    arrayToRemoveFrom,
    arrayToAddInto
  };

  setCardIsFlipped(false);
  setRefreshCounter(refreshCounter + 1);
  dispatch(updateArraysAction(updatedArrays));
};

export const handleFirstMove = () => {
  switch (status) {
    case "todo":
      return selectChange(todoArray, inProgressArray, "inProgress");
      break;
    case "inProgress":
      return selectChange(inProgressArray, completedTasksArray, "done");
      break;
    case "done":
      return selectChange(completedTasksArray, inProgressArray, "inProgress");
      break;
    default:
      break;
  }
};

export const handleSecondMove = () => {
  switch (status) {
    case "todo":
      return selectChange(todoArray, completedTasksArray, "done");
      break;
    case "inProgress":
      return selectChange(inProgressArray, todoArray, "todo");
      break;
    case "done":
      return selectChange(completedTasksArray, todoArray, "todo");
      break;
    default:
      break;
  }
};

export const handleDelete = () => {
  switch (status) {
    case "todo":
      return selectChange(todoArray, null, null, true);
      break;
    case "inProgress":
      return selectChange(inProgressArray, null, null, true);
      break;
    case "done":
      return selectChange(completedTasksArray, null, null, true);
      break;
    default:
      break;
  }
};
