import { useDispatch } from "react-redux";
import _ from "lodash";

import { updateArraysAction } from "../../../state/actions/updateArraysAction";

const dispatch = useDispatch();

//---UPDATEING DATABASE---///

export const updatingArraysInDataBase = async (
  arrayToRemoveFrom,
  arrayToAddInto
) => {
  const updatedArrays = {
    arrayToRemoveFrom,
    arrayToAddInto
  };

  const putMethod = {
    method: "PUT",
    headers: {
      "Content-type": "application/json; charset=UTF-8",
      "auth-token": localStorage.getItem("auth-token")
    },
    body: JSON.stringify(updatedArrays) // We send data in JSON format
  };

  await fetch(
    `https://tracker-database.youngwebdevs.now.sh/dashboard/updatingarrays`,
    putMethod
  );
};
export const deletingFromArrayInDataBase = async arrayToRemoveFrom => {
  const updatedArray = {
    arrayToRemoveFrom
  };

  const putMethod = {
    method: "PUT",
    headers: {
      "Content-type": "application/json; charset=UTF-8",
      "auth-token": localStorage.getItem("auth-token")
    },
    body: JSON.stringify(updatedArray) // We send data in JSON format
  };

  await fetch(
    `https://tracker-database.youngwebdevs.now.sh/dashboard/removetask`,
    putMethod
  );
};

//---UPDATING STATE ARRAYS--//

export const selectChange = (
  arrayToRemoveFrom,
  arrayToAddInto,
  destination,
  deleteTask,
  taskId,
  setCardIsFlipped
) => {
  const selectedTask = _.remove(arrayToRemoveFrom, n => {
    return n.id === taskId;
  });
  console.log("selectedtask: ", selectedTask[0]);

  if (deleteTask === true) {
    setCardIsFlipped(false);
    dispatch(updateArraysAction(arrayToRemoveFrom));
    return;
  }

  const newStatusSelectedTask = {
    ...selectedTask[0],
    status: destination
  };
  arrayToAddInto.push(newStatusSelectedTask);

  const updatedArrays = {
    arrayToRemoveFrom,
    arrayToAddInto
  };

  setCardIsFlipped(false);
  dispatch(updateArraysAction(updatedArrays));
};

//---UPDATING FIRST MOVE--//

export const handleFirstMove = (status, arrayToRemoveFrom, arrayToAddInto) => {
  switch (status) {
    case "todo":
      //todoArray, inProgressArray
      selectChange(arrayToRemoveFrom, arrayToAddInto, "inProgress");
      return updatingArraysInDataBase(arrayToRemoveFrom, arrayToAddInto);
      break;
    case "inProgress":
      //inProgressArray, completedTasksArray
      updatingArraysInDataBase(arrayToRemoveFrom, arrayToAddInto);
      return selectChange(arrayToRemoveFrom, arrayToAddInto, "done");
      break;
    case "done":
      //completedTasksArray, inProgressArray
      updatingArraysInDataBase(arrayToRemoveFrom, arrayToAddInto);
      return selectChange(arrayToRemoveFrom, arrayToAddInto, "inProgress");
      break;
    default:
      break;
  }
};

//---UPDATING SECOND MOVE--//

export const handleSecondMove = (status, arrayToRemoveFrom, arrayToAddInto) => {
  switch (status) {
    case "todo":
      //todoArray,completedTasksArray
      updatingArraysInDataBase(arrayToRemoveFrom, arrayToAddInto);
      return selectChange(arrayToRemoveFrom, arrayToAddInto, "done");
      break;
    case "inProgress":
      //inProgressArray,todoArray
      updatingArraysInDataBase(arrayToRemoveFrom, arrayToAddInto);
      return selectChange(arrayToRemoveFrom, arrayToAddInto, "todo");
      break;
    case "done":
      //completedTasksArray,todoArray
      updatingArraysInDataBase(arrayToRemoveFrom, arrayToAddInto);
      return selectChange(arrayToRemoveFrom, arrayToAddInto, "todo");
      break;
    default:
      break;
  }
};

//---HANDLE DELETED TASK--//

export const handleDelete = (status, arrayToRemoveFrom) => {
  switch (status) {
    case "todo":
      //todoArray
      deletingFromArrayInDataBase(arrayToRemoveFrom);
      return selectChange(arrayToRemoveFrom, null, null, true);
      break;
    case "inProgress":
      //inProgressArray
      deletingFromArrayInDataBase(arrayToRemoveFrom);
      return selectChange(arrayToRemoveFrom, null, null, true);
      break;
    case "done":
      //completedTasksArray
      deletingFromArrayInDataBase(arrayToRemoveFrom);
      return selectChange(arrayToRemoveFrom, null, null, true);
      break;
    default:
      break;
  }
};
